import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Navbar, Nav, Container, Badge, Dropdown, Form, InputGroup, Button } from 'react-bootstrap';
import { FaShoppingCart, FaUser, FaSearch, FaBars } from 'react-icons/fa';
import NotificationBell from '../NotificationBell';

const Header = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [cartCount] = useState(3); // Mock cart count
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Auth state
  const [user, setUser] = useState(null); // User state

  useEffect(() => {
    // Check if user is logged in from localStorage
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      const userData = JSON.parse(savedUser);
      setUser(userData);
      setIsAuthenticated(true);
    }
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/books?q=${encodeURIComponent(searchQuery.trim())}`);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
    navigate('/');
  };

  return (
    <Navbar expand="lg" className="navbar-modern">
      <Container fluid>
        {/* Logo */}
        <Navbar.Brand as={Link} to="/" className="navbar-brand-modern">
          <div className="brand-logo">
            <span className="logo-text">BookStore</span>
          </div>
        </Navbar.Brand>

        {/* Desktop Search Bar */}
        <Form onSubmit={handleSearch} className="d-none d-lg-flex desktop-search">
          <InputGroup>
            <Form.Control
              type="search"
              placeholder="Tìm kiếm sách..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <Button variant="outline-light" type="submit" className="search-btn">
              <FaSearch />
            </Button>
          </InputGroup>
        </Form>

        {/* Desktop Actions */}
        <div className="d-none d-lg-flex desktop-actions">
          <Link to="/cart" className="action-btn cart-btn">
            <FaShoppingCart />
            {cartCount > 0 && (
              <Badge bg="danger" className="cart-badge">{cartCount}</Badge>
            )}
          </Link>

          <NotificationBell />

          {isAuthenticated ? (
            <Dropdown align="end">
              <Dropdown.Toggle as={Button} variant="link" className="user-dropdown-btn">
                <FaUser className="me-2" />
                {user?.fullName || user?.firstName || 'User'}
              </Dropdown.Toggle>
              <Dropdown.Menu className="user-dropdown-menu">
                <Dropdown.Item as={Link} to="/profile">
                  Thông tin cá nhân
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="/orders">
                  Đơn hàng của tôi
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item onClick={handleLogout}>
                  Đăng xuất
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          ) : (
            <Button as={Link} to="/login" variant="outline-light" className="login-btn">
              Đăng nhập
            </Button>
          )}
        </div>

        {/* Mobile Toggle */}
        <Navbar.Toggle aria-controls="basic-navbar-nav" className="mobile-toggle">
          <FaBars />
        </Navbar.Toggle>

        {/* Mobile Menu */}
        <Navbar.Collapse id="basic-navbar-nav" className="mobile-menu">
          {/* Mobile Search */}
          <Form onSubmit={handleSearch} className="mobile-search">
            <InputGroup>
              <Form.Control
                type="search"
                placeholder="Tìm kiếm sách..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="mobile-search-input"
              />
              <Button variant="outline-light" type="submit" className="mobile-search-btn">
                <FaSearch />
              </Button>
            </InputGroup>
          </Form>

          {/* Mobile Navigation */}
          <Nav className="mobile-nav">
            <Nav.Link as={Link} to="/" className="mobile-nav-link">
              Trang chủ
            </Nav.Link>
            <Nav.Link as={Link} to="/books" className="mobile-nav-link">
              Sách
            </Nav.Link>
            <Nav.Link as={Link} to="/categories" className="mobile-nav-link">
              Danh mục
            </Nav.Link>
            <Nav.Link as={Link} to="/about" className="mobile-nav-link">
              Giới thiệu
            </Nav.Link>
          </Nav>

          {/* Mobile Actions */}
          <div className="mobile-actions">
            <Link to="/cart" className="mobile-action-btn">
              <FaShoppingCart />
              <span>Giỏ hàng</span>
              {cartCount > 0 && (
                <Badge bg="danger" className="mobile-cart-badge">{cartCount}</Badge>
              )}
            </Link>

            <NotificationBell />

            {isAuthenticated ? (
              <div className="mobile-user-menu">
                <div className="user-info">
                  <FaUser />
                  <span>{user?.fullName || user?.firstName || 'User'}</span>
                </div>
                <div className="user-menu-links">
                  <Link to="/profile" className="user-menu-link">
                    Thông tin cá nhân
                  </Link>
                  <Link to="/orders" className="user-menu-link">
                    Đơn hàng của tôi
                  </Link>
                  <button onClick={handleLogout} className="user-menu-link logout-btn">
                    Đăng xuất
                  </button>
                </div>
              </div>
            ) : (
              <Link to="/login" className="mobile-login-btn">
                Đăng nhập
              </Link>
            )}
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
