import React, { useState, useEffect } from 'react';
import { Button, Badge } from 'react-bootstrap';
import { FaBell } from 'react-icons/fa';
import { Link } from 'react-router-dom';

const NotificationBell = () => {
  const [unreadCount, setUnreadCount] = useState(0);

  // Mock notifications data - same as NotificationPage
  const mockNotifications = [
    {
      id: 1,
      type: 'success',
      title: 'Đặt hàng thành công',
      message: 'Đơn hàng #12345 đã được xác nhận và đang chuẩn bị giao hàng.',
      time: '2 phút trước',
      read: false,
      category: 'Đơn hàng',
      priority: 'high'
    },
    {
      id: 2,
      type: 'warning',
      title: 'Sản phẩm sắp hết hàng',
      message: 'Sách "React từ cơ bản" chỉ còn 3 cuốn trong kho.',
      time: '1 giờ trước',
      read: false,
      category: 'Kho hàng',
      priority: 'medium'
    },
    {
      id: 3,
      type: 'info',
      title: 'Khuyến mãi mới',
      message: 'Giảm giá 20% cho tất cả sách công nghệ trong tuần này.',
      time: '3 giờ trước',
      read: true,
      category: 'Khuyến mãi',
      priority: 'low'
    },
    {
      id: 4,
      type: 'success',
      title: 'Thanh toán thành công',
      message: 'Giao dịch #67890 đã được xử lý thành công.',
      time: '5 giờ trước',
      read: true,
      category: 'Thanh toán',
      priority: 'high'
    },
    {
      id: 5,
      type: 'info',
      title: 'Cập nhật hệ thống',
      message: 'Hệ thống sẽ được bảo trì từ 02:00 - 04:00 ngày mai.',
      time: '1 ngày trước',
      read: true,
      category: 'Hệ thống',
      priority: 'medium'
    },
    {
      id: 6,
      type: 'warning',
      title: 'Địa chỉ giao hàng không chính xác',
      message: 'Chúng tôi không thể giao hàng đến địa chỉ bạn đã cung cấp.',
      time: '2 ngày trước',
      read: false,
      category: 'Giao hàng',
      priority: 'high'
    }
  ];

  useEffect(() => {
    // Calculate unread count from mock data
    const unread = mockNotifications.filter(notification => !notification.read).length;
    setUnreadCount(unread);
  }, []);

  return (
    <Link to="/notifications" className="text-white text-decoration-none">
      <Button variant="link" className="text-white position-relative">
        <FaBell />
        {unreadCount > 0 && (
          <Badge bg="danger" className="position-absolute top-0 end-0">
            {unreadCount > 99 ? '99+' : unreadCount}
          </Badge>
        )}
      </Button>
    </Link>
  );
};

export default NotificationBell;
