import React from 'react';
import { Button, Card, Row, Col } from 'react-bootstrap';
import { useNotifications, NOTIFICATION_MESSAGES } from '../hooks/useNotifications';

const NotificationDemo = () => {
  const { addNotification } = useNotifications();

  const handleTestNotification = (type) => {
    switch (type) {
      case 'success':
        addNotification(NOTIFICATION_MESSAGES.ORDER_SUCCESS);
        break;
      case 'warning':
        addNotification(NOTIFICATION_MESSAGES.LOW_STOCK);
        break;
      case 'info':
        addNotification(NOTIFICATION_MESSAGES.PROMOTION);
        break;
      case 'error':
        addNotification(NOTIFICATION_MESSAGES.PAYMENT_FAILED);
        break;
      default:
        addNotification({
          type: 'info',
          title: 'Thông báo test',
          message: 'Đây là thông báo test từ demo component.'
        });
    }
  };

  return (
    <Card className="mt-4">
      <Card.Header>
        <h5 className="mb-0">Demo Hệ thống Thông báo</h5>
      </Card.Header>
      <Card.Body>
        <p className="text-muted mb-3">
          Click các nút bên dưới để test các loại thông báo khác nhau:
        </p>
        
        <Row className="g-2">
          <Col md={3} sm={6}>
            <Button 
              variant="success" 
              className="w-100"
              onClick={() => handleTestNotification('success')}
            >
              Thông báo Thành công
            </Button>
          </Col>
          <Col md={3} sm={6}>
            <Button 
              variant="warning" 
              className="w-100"
              onClick={() => handleTestNotification('warning')}
            >
              Thông báo Cảnh báo
            </Button>
          </Col>
          <Col md={3} sm={6}>
            <Button 
              variant="info" 
              className="w-100"
              onClick={() => handleTestNotification('info')}
            >
              Thông báo Thông tin
            </Button>
          </Col>
          <Col md={3} sm={6}>
            <Button 
              variant="danger" 
              className="w-100"
              onClick={() => handleTestNotification('error')}
            >
              Thông báo Lỗi
            </Button>
          </Col>
        </Row>

        <div className="mt-3">
          <small className="text-muted">
            <strong>Hướng dẫn:</strong>
            <ul className="mt-2">
              <li>Click nút để tạo thông báo toast (hiện ở góc phải màn hình)</li>
              <li>Click vào icon chuông ở header để xem dropdown thông báo</li>
              <li>Click vào thông báo để đánh dấu đã đọc</li>
              <li>Click X để xóa thông báo</li>
            </ul>
          </small>
        </div>
      </Card.Body>
    </Card>
  );
};

export default NotificationDemo;
