    },
    {
      id: 4,
      type: 'success',
      title: 'Thanh to√°n th√†nh c√¥ng',
      message: 'Giao d·ªãch #67890 ƒë√£ ƒë∆∞·ª£c x·ª≠ l√Ω th√†nh c√¥ng. S·ªë ti·ªÅn 450,000 VNƒê ƒë√£ ƒë∆∞·ª£c thanh to√°n.',
      time: '5 gi·ªù tr∆∞·ªõc',
      read: true,
      category: 'Thanh to√°n',
      priority: 'high'
    },
    {
      id: 5,
      type: 'info',
      title: 'C·∫≠p nh·∫≠t h·ªá th·ªëng',
      message: 'H·ªá th·ªëng s·∫Ω ƒë∆∞·ª£c b·∫£o tr√¨ t·ª´ 02:00 - 04:00 ng√†y mai. Trong th·ªùi gian n√†y, m·ªôt s·ªë t√≠nh nƒÉng c√≥ th·ªÉ kh√¥ng kh·∫£ d·ª•ng.',
      time: '1 ng√†y tr∆∞·ªõc',
      read: true,
      category: 'H·ªá th·ªëng',
      priority: 'medium'
    },
    {
      id: 6,
      type: 'warning',
      title: 'ƒê·ªãa ch·ªâ giao h√†ng kh√¥ng ch√≠nh x√°c',
      message: 'Ch√∫ng t√¥i kh√¥ng th·ªÉ giao h√†ng ƒë·∫øn ƒë·ªãa ch·ªâ b·∫°n ƒë√£ cung c·∫•p. Vui l√≤ng c·∫≠p nh·∫≠t ƒë·ªãa ch·ªâ giao h√†ng.',
      time: '2 ng√†y tr∆∞·ªõc',
      read: false,
      category: 'Giao h√†ng',
      priority: 'high'
    }
  ];

  useEffect(() => {
    setLoading(true);
    // Simulate API call
    setTimeout(() => {
      setNotifications(mockNotifications);
      setLoading(false);
    }, 1000);
  }, []);

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'success':
        return <FaCheckCircle className="text-success" />;
      case 'warning':
        return <FaExclamationTriangle className="text-warning" />;
      case 'info':
        return <FaInfoCircle className="text-info" />;
      default:
        return <FaBell className="text-primary" />;
    }
  };

  const getCategoryBadgeColor = (category) => {
    switch (category) {
      case 'ƒê∆°n h√†ng':
        return 'primary';
      case 'Kho h√†ng':
        return 'warning';
      case 'Khuy·∫øn m√£i':
        return 'success';
      case 'Thanh to√°n':
        return 'info';
      case 'H·ªá th·ªëng':
        return 'secondary';
      case 'Giao h√†ng':
        return 'danger';
      default:
        return 'secondary';
    }
  };

  const getPriorityBadgeColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'danger';
      case 'medium':
        return 'warning';
      case 'low':
        return 'success';
      default:
        return 'secondary';
    }
  };

  const filteredNotifications = notifications.filter(notification => {
    const matchesFilter = filter === 'all' || 
      (filter === 'unread' && !notification.read) || 
      (filter === 'read' && notification.read);
    
    const matchesSearch = notification.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      notification.message.toLowerCase().includes(searchQuery.toLowerCase()) ||
      notification.category.toLowerCase().includes(searchQuery.toLowerCase());
    
    return matchesFilter && matchesSearch;
  });

  const unreadCount = notifications.filter(n => !n.read).length;

  const markAsRead = (id) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === id 
          ? { ...notification, read: true }
          : notification
      )
    );
  };

  const markAsUnread = (id) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === id 
          ? { ...notification, read: false }
          : notification
      )
    );
  };

  const deleteNotification = (id) => {
    setNotifications(prev => prev.filter(notification => notification.id !== id));
  };

  const markAllAsRead = () => {
    setNotifications(prev => 
      prev.map(notification => ({ ...notification, read: true }))
    );
  };

  const toggleSelectNotification = (id) => {
    setSelectedNotifications(prev => 
      prev.includes(id) 
        ? prev.filter(notificationId => notificationId !== id)
        : [...prev, id]
    );
  };

  const selectAllNotifications = () => {
    setSelectedNotifications(filteredNotifications.map(n => n.id));
  };

  const deselectAllNotifications = () => {
    setSelectedNotifications([]);
  };

  const deleteSelectedNotifications = () => {
    setNotifications(prev => prev.filter(notification => !selectedNotifications.includes(notification.id)));
    setSelectedNotifications([]);
  };

  const markSelectedAsRead = () => {
    setNotifications(prev => 
      prev.map(notification => 
        selectedNotifications.includes(notification.id)
          ? { ...notification, read: true }
          : notification
      )
    );
    setSelectedNotifications([]);
  };

  const NotificationItem = ({ notification }) => (
    <Card className={`mb-3 ${!notification.read ? 'border-primary' : ''}`}>
      <Card.Body>
        <Row className="align-items-start">
          <Col xs={1} className="text-center">
            <div className="mt-1">
              {getNotificationIcon(notification.type)}
            </div>
          </Col>
          <Col xs={1} className="text-center">
            <Form.Check
              type="checkbox"
              checked={selectedNotifications.includes(notification.id)}
              onChange={() => toggleSelectNotification(notification.id)}
            />
          </Col>
          <Col xs={8}>
            <div className="d-flex align-items-center mb-2">
              <h6 className={`mb-0 me-2 ${!notification.read ? 'fw-bold' : ''}`}>
                {notification.title}
              </h6>
              <Badge bg={getCategoryBadgeColor(notification.category)} className="me-2">
                {notification.category}
              </Badge>
              <Badge bg={getPriorityBadgeColor(notification.priority)}>
                {notification.priority}
              </Badge>
            </div>
            <p className="text-muted mb-2">
              {notification.message}
            </p>
            <small className="text-muted">{notification.time}</small>
          </Col>
          <Col xs={2} className="text-end">
            <div className="d-flex flex-column gap-1">
              {!notification.read ? (
                <Button
                  variant="outline-primary"
                  size="sm"
                  onClick={() => markAsRead(notification.id)}
                >
                  <FaCheck />
                </Button>
              ) : (
                <Button
                  variant="outline-secondary"
                  size="sm"
                  onClick={() => markAsUnread(notification.id)}
                >
                  ƒê√°nh d·∫•u ch∆∞a ƒë·ªçc
                </Button>
              )}
              <Button
                variant="outline-danger"
                size="sm"
                onClick={() => deleteNotification(notification.id)}
              >
                <FaTimes />
              </Button>
            </div>
          </Col>
        </Row>
      </Card.Body>
    </Card>
  );

  if (loading) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">ƒêang t·∫£i...</span>
          </Spinner>
          <p className="mt-3">ƒêang t·∫£i th√¥ng b√°o...</p>
        </div>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      {/* Header */}
      <Row className="mb-4">
        <Col>
          <h1 className="h2 fw-bold">Th√¥ng b√°o</h1>
          <p className="text-muted">
            Qu·∫£n l√Ω v√† theo d√µi t·∫•t c·∫£ th√¥ng b√°o c·ªßa b·∫°n
            {unreadCount > 0 && (
              <Badge bg="danger" className="ms-2">
                {unreadCount} ch∆∞a ƒë·ªçc
              </Badge>
            )}
          </p>
        </Col>
      </Row>

      {/* Filters and Actions */}
      <Row className="mb-4">
        <Col md={6}>
          <div className="d-flex gap-2">
            <Button
              variant={filter === 'all' ? 'primary' : 'outline-primary'}
              size="sm"
              onClick={() => setFilter('all')}
            >
              T·∫•t c·∫£ ({notifications.length})
            </Button>
            <Button
              variant={filter === 'unread' ? 'primary' : 'outline-primary'}
              size="sm"
              onClick={() => setFilter('unread')}
            >
              Ch∆∞a ƒë·ªçc ({unreadCount})
            </Button>
            <Button
              variant={filter === 'read' ? 'primary' : 'outline-primary'}
              size="sm"
              onClick={() => setFilter('read')}
            >
              ƒê√£ ƒë·ªçc ({notifications.length - unreadCount})
            </Button>
          </div>
        </Col>
        <Col md={6}>
          <div className="d-flex gap-2 justify-content-end">
            {selectedNotifications.length > 0 && (
              <>
                <Button
                  variant="outline-success"
                  size="sm"
                  onClick={markSelectedAsRead}
                >
                  <FaCheck className="me-1" />
                  ƒê√°nh d·∫•u ƒë√£ ƒë·ªçc ({selectedNotifications.length})
                </Button>
                <Button
                  variant="outline-danger"
                  size="sm"
                  onClick={deleteSelectedNotifications}
                >
                  <FaTrash className="me-1" />
                  X√≥a ({selectedNotifications.length})
                </Button>
              </>
            )}
            <Button
              variant="outline-primary"
              size="sm"
              onClick={markAllAsRead}
              disabled={unreadCount === 0}
            >
              <FaCheckCircle className="me-1" />
              ƒê·ªçc t·∫•t c·∫£
            </Button>
          </div>
        </Col>
      </Row>

      {/* Search */}
      <Row className="mb-4">
        <Col md={6}>
          <InputGroup>
            <InputGroup.Text>
              <FaSearch />
            </InputGroup.Text>
            <Form.Control
              type="text"
              placeholder="T√¨m ki·∫øm th√¥ng b√°o..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </InputGroup>
        </Col>
        <Col md={6} className="text-end">
          <div className="d-flex gap-2 justify-content-end">
            <Button
              variant="outline-secondary"
              size="sm"
              onClick={selectAllNotifications}
              disabled={filteredNotifications.length === 0}
            >
              Ch·ªçn t·∫•t c·∫£
            </Button>
            <Button
              variant="outline-secondary"
              size="sm"
              onClick={deselectAllNotifications}
              disabled={selectedNotifications.length === 0}
            >
              B·ªè ch·ªçn t·∫•t c·∫£
            </Button>
          </div>
        </Col>
      </Row>

      {/* Notifications List */}
      {filteredNotifications.length === 0 ? (
        <Row>
          <Col className="text-center py-5">
            <div className="fs-1 mb-3">üîî</div>
            <h4>
              {searchQuery ? 'Kh√¥ng t√¨m th·∫•y th√¥ng b√°o' : 'Kh√¥ng c√≥ th√¥ng b√°o'}
            </h4>
            <p className="text-muted mb-4">
              {searchQuery 
                ? 'Th·ª≠ thay ƒë·ªïi t·ª´ kh√≥a t√¨m ki·∫øm ho·∫∑c b·ªô l·ªçc'
                : 'B·∫°n ch∆∞a c√≥ th√¥ng b√°o n√†o'
