import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Spinner, Modal, Badge } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import { FaCreditCard, FaMapMarkerAlt, FaPhone, FaEnvelope, FaUser, FaLock, FaCheckCircle } from 'react-icons/fa';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [orderNumber, setOrderNumber] = useState('');
  
  // Form states
  const [shippingInfo, setShippingInfo] = useState({
    fullName: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    zipCode: '',
    notes: ''
  });
  
  const [paymentMethod, setPaymentMethod] = useState('cod');
  const [cardInfo, setCardInfo] = useState({
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardName: ''
  });
  
  const [errors, setErrors] = useState({});
  const [sameAsShipping, setSameAsShipping] = useState(true);

  // Mock cart data
  const mockCartItems = [
    {
      id: 1,
      book: {
        id: 1,
        title: "React.js Complete Guide",
        author: "Maximilian Schwarzmüller",
        price: 500000,
        image: "https://via.placeholder.com/300x400/6366f1/ffffff?text=React+Guide",
        category: "Công nghệ"
      },
      quantity: 2
    },
    {
      id: 2,
      book: {
        id: 2,
        title: "JavaScript ES6+ Mastery",
        author: "Kyle Simpson",
        price: 400000,
        image: "https://via.placeholder.com/300x400/ec4899/ffffff?text=JS+ES6",
        category: "Lập trình"
      },
      quantity: 1
    }
  ];

  const [appliedCoupon] = useState({ type: 'percentage', value: 10 });

  useEffect(() => {
    // Load user info if available
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    if (user.name) {
      setShippingInfo(prev => ({
        ...prev,
        fullName: user.name,
        email: user.email || '',
        phone: user.phone || ''
      }));
    }
  }, []);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND'
    }).format(price);
  };

  const calculateSubtotal = () => {
    return mockCartItems.reduce((total, item) => total + (item.book.price * item.quantity), 0);
  };

  const calculateTax = () => {
    return calculateSubtotal() * 0.1;
  };

  const calculateShipping = () => {
    return calculateSubtotal() >= 500000 ? 0 : 30000;
  };

  const calculateDiscount = () => {
    if (appliedCoupon) {
      return appliedCoupon.type === 'percentage' 
        ? calculateSubtotal() * (appliedCoupon.value / 100)
        : appliedCoupon.value;
    }
    return 0;
  };

  const calculateTotal = () => {
    return calculateSubtotal() + calculateTax() + calculateShipping() - calculateDiscount();
  };

  const handleShippingChange = (e) => {
    const { name, value } = e.target;
    setShippingInfo(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleCardChange = (e) => {
    const { name, value } = e.target;
    setCardInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    // Shipping validation
    if (!shippingInfo.fullName.trim()) {
      newErrors.fullName = 'Họ tên là bắt buộc';
    }
    if (!shippingInfo.email) {
      newErrors.email = 'Email là bắt buộc';
    } else if (!/\S+@\S+\.\S+/.test(shippingInfo.email)) {
      newErrors.email = 'Email không hợp lệ';
    }
    if (!shippingInfo.phone) {
      newErrors.phone = 'Số điện thoại là bắt buộc';
    } else if (!/^[0-9]{10,11}$/.test(shippingInfo.phone.replace(/\s/g, ''))) {
      newErrors.phone = 'Số điện thoại không hợp lệ';
    }
    if (!shippingInfo.address.trim()) {
      newErrors.address = 'Địa chỉ là bắt buộc';
    }
    if (!shippingInfo.city.trim()) {
      newErrors.city = 'Thành phố là bắt buộc';
    }

    // Payment validation
    if (paymentMethod === 'card') {
      if (!cardInfo.cardNumber) {
        newErrors.cardNumber = 'Số thẻ là bắt buộc';
      } else if (!/^[0-9]{16}$/.test(cardInfo.cardNumber.replace(/\s/g, ''))) {
        newErrors.cardNumber = 'Số thẻ không hợp lệ';
      }
      if (!cardInfo.expiryDate) {
        newErrors.expiryDate = 'Ngày hết hạn là bắt buộc';
      } else if (!/^(0[1-9]|1[0-2])\/\d{2}$/.test(cardInfo.expiryDate)) {
        newErrors.expiryDate = 'Định dạng MM/YY';
      }
      if (!cardInfo.cvv) {
        newErrors.cvv = 'CVV là bắt buộc';
      } else if (!/^[0-9]{3,4}$/.test(cardInfo.cvv)) {
        newErrors.cvv = 'CVV không hợp lệ';
      }
      if (!cardInfo.cardName.trim()) {
        newErrors.cardName = 'Tên trên thẻ là bắt buộc';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    
    // Simulate API call
    setTimeout(() => {
      const orderId = 'ORD' + Date.now();
      setOrderNumber(orderId);
      setLoading(false);
      setShowSuccessModal(true);
      
      // Clear cart after successful order
      localStorage.removeItem('cart');
    }, 2000);
  };

  const OrderItem = ({ item }) => (
    <div className="d-flex align-items-center border-bottom py-3">
      <img
        src={item.book.image}
        alt={item.book.title}
        className="rounded me-3"
        style={{ width: '60px', height: '80px', objectFit: 'cover' }}
      />
      <div className="flex-grow-1">
        <h6 className="mb-1">{item.book.title}</h6>
        <p className="text-muted small mb-1">Tác giả: {item.book.author}</p>
        <Badge bg="secondary" className="small">
          {item.book.category}
        </Badge>
      </div>
      <div className="text-end">
        <div className="fw-bold">{formatPrice(item.book.price * item.quantity)}</div>
        <small className="text-muted">x{item.quantity}</small>
      </div>
    </div>
  );

  return (
    <Container className="py-4">
      {/* Header */}
      <Row className="mb-4">
        <Col>
          <h1 className="h2 fw-bold">Thanh toán</h1>
          <p className="text-muted">Hoàn tất đơn hàng của bạn</p>
        </Col>
      </Row>

      <Form onSubmit={handleSubmit}>
        <Row>
          {/* Left Column - Forms */}
          <Col lg={8}>
            {/* Shipping Information */}
            <Card className="mb-4">
              <Card.Header>
                <h5 className="mb-0">
                  <FaMapMarkerAlt className="me-2" />
                  Thông tin giao hàng
                </h5>
              </Card.Header>
              <Card.Body>
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>
                        <FaUser className="me-1" />
                        Họ và tên *
                      </Form.Label>
                      <Form.Control
                        type="text"
                        name="fullName"
                        value={shippingInfo.fullName}
                        onChange={handleShippingChange}
                        placeholder="Nhập họ tên"
                        isInvalid={!!errors.fullName}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.fullName}
                      </Form.Control.Feedback>
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>
                        <FaPhone className="me-1" />
                        Số điện thoại *
                      </Form.Label>
                      <Form.Control
                        type="tel"
                        name="phone"
                        value={shippingInfo.phone}
                        onChange={handleShippingChange}
                        placeholder="Nhập số điện thoại"
                        isInvalid={!!errors.phone}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.phone}
                      </Form.Control.Feedback>
                    </Form.Group>
                  </Col>
                </Row>

                <Form.Group className="mb-3">
                  <Form.Label>
                    <FaEnvelope className="me-1" />
                    Email *
                  </Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={shippingInfo.email}
                    onChange={handleShippingChange}
                    placeholder="Nhập email"
                    isInvalid={!!errors.email}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.email}
                  </Form.Control.Feedback>
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Địa chỉ giao hàng *</Form.Label>
                  <Form.Control
                    type="text"
                    name="address"
                    value={shippingInfo.address}
                    onChange={handleShippingChange}
                    placeholder="Nhập địa chỉ chi tiết"
                    isInvalid={!!errors.address}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.address}
                  </Form.Control.Feedback>
                </Form.Group>

                <Row>
                  <Col md={8}>
                    <Form.Group className="mb-3">
                      <Form.Label>Thành phố *</Form.Label>
                      <Form.Control
                        type="text"
                        name="city"
                        value={shippingInfo.city}
                        onChange={handleShippingChange}
                        placeholder="Nhập thành phố"
                        isInvalid={!!errors.city}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.city}
                      </Form.Control.Feedback>
                    </Form.Group>
                  </Col>
                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label>Mã bưu điện</Form.Label>
                      <Form.Control
                        type="text"
                        name="zipCode"
                        value={shippingInfo.zipCode}
                        onChange={handleShippingChange}
                        placeholder="Mã bưu điện"
                      />
                    </Form.Group>
                  </Col>
                </Row>

                <Form.Group className="mb-3">
                  <Form.Label>Ghi chú đơn hàng</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name="notes"
                    value={shippingInfo.notes}
                    onChange={handleShippingChange}
                    placeholder="Ghi chú cho đơn hàng (tùy chọn)"
                  />
                </Form.Group>
              </Card.Body>
            </Card>

            {/* Payment Method */}
            <Card className="mb-4">
              <Card.Header>
                <h5 className="mb-0">
                  <FaCreditCard className="me-2" />
                  Phương thức thanh toán
                </h5>
              </Card.Header>
              <Card.Body>
                <div className="mb-3">
                  <Form.Check
                    type="radio"
                    id="cod"
                    name="paymentMethod"
                    value="cod"
                    checked={paymentMethod === 'cod'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    label={
                      <div>
                        <strong>Thanh toán khi nhận hàng (COD)</strong>
                        <small className="text-muted d-block">Thanh toán bằng tiền mặt khi nhận hàng</small>
                      </div>
                    }
                  />
                </div>

                <div className="mb-3">
                  <Form.Check
                    type="radio"
                    id="card"
                    name="paymentMethod"
                    value="card"
                    checked={paymentMethod === 'card'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    label={
                      <div>
                        <strong>Thẻ tín dụng/ghi nợ</strong>
                        <small className="text-muted d-block">Thanh toán bằng thẻ Visa, Mastercard</small>
                      </div>
                    }
                  />
                </div>

                {paymentMethod === 'card' && (
                  <div className="border p-3 rounded bg-light">
                    <Row>
                      <Col md={12}>
                        <Form.Group className="mb-3">
                          <Form.Label>Số thẻ *</Form.Label>
                          <Form.Control
                            type="text"
                            name="cardNumber"
                            value={cardInfo.cardNumber}
                            onChange={handleCardChange}
                            placeholder="1234 5678 9012 3456"
                            maxLength="19"
                            isInvalid={!!errors.cardNumber}
                          />
                          <Form.Control.Feedback type="invalid">
                            {errors.cardNumber}
                          </Form.Control.Feedback>
                        </Form.Group>
                      </Col>
                    </Row>
                    <Row>
                      <Col md={6}>
                        <Form.Group className="mb-3">
                          <Form.Label>Ngày hết hạn *</Form.Label>
                          <Form.Control
                            type="text"
                            name="expiryDate"
                            value={cardInfo.expiryDate}
                            onChange={handleCardChange}
                            placeholder="MM/YY"
                            maxLength="5"
                            isInvalid={!!errors.expiryDate}
                          />
                          <Form.Control.Feedback type="invalid">
                            {errors.expiryDate}
                          </Form.Control.Feedback>
                        </Form.Group>
                      </Col>
                      <Col md={6}>
                        <Form.Group className="mb-3">
                          <Form.Label>CVV *</Form.Label>
                          <Form.Control
                            type="text"
                            name="cvv"
                            value={cardInfo.cvv}
                            onChange={handleCardChange}
                            placeholder="123"
                            maxLength="4"
                            isInvalid={!!errors.cvv}
                          />
                          <Form.Control.Feedback type="invalid">
                            {errors.cvv}
                          </Form.Control.Feedback>
                        </Form.Group>
                      </Col>
                    </Row>
                    <Form.Group className="mb-3">
                      <Form.Label>Tên trên thẻ *</Form.Label>
                      <Form.Control
                        type="text"
                        name="cardName"
                        value={cardInfo.cardName}
                        onChange={handleCardChange}
                        placeholder="NGUYEN VAN A"
                        isInvalid={!!errors.cardName}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.cardName}
                      </Form.Control.Feedback>
                    </Form.Group>
                  </div>
                )}
              </Card.Body>
            </Card>
          </Col>

          {/* Right Column - Order Summary */}
          <Col lg={4}>
            <Card className="sticky-top" style={{ top: '20px' }}>
              <Card.Header>
                <h5 className="mb-0">Tóm tắt đơn hàng</h5>
              </Card.Header>
              <Card.Body>
                {/* Order Items */}
                <div className="mb-3">
                  {mockCartItems.map((item) => (
                    <OrderItem key={item.id} item={item} />
                  ))}
                </div>

                <hr />

                {/* Pricing */}
                <div className="d-flex justify-content-between mb-2">
                  <span>Tạm tính:</span>
                  <span>{formatPrice(calculateSubtotal())}</span>
                </div>
                
                <div className="d-flex justify-content-between mb-2">
                  <span>Thuế (10%):</span>
                  <span>{formatPrice(calculateTax())}</span>
                </div>
                
                <div className="d-flex justify-content-between mb-2">
                  <span>Phí ship:</span>
                  <span>
                    {calculateShipping() === 0 ? (
                      <span className="text-success">Miễn phí</span>
                    ) : (
                      formatPrice(calculateShipping())
                    )}
                  </span>
                </div>

                {appliedCoupon && (
                  <div className="d-flex justify-content-between mb-2">
                    <span>Giảm giá:</span>
                    <span className="text-success">
                      -{formatPrice(calculateDiscount())}
                    </span>
                  </div>
                )}

                <hr />
                <div className="d-flex justify-content-between mb-3">
                  <strong>Tổng cộng:</strong>
                  <strong className="text-primary fs-5">
                    {formatPrice(calculateTotal())}
                  </strong>
                </div>

                {/* Submit Button */}
                <Button
                  type="submit"
                  variant="primary"
                  size="lg"
                  className="w-100 mb-3"
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <Spinner size="sm" className="me-2" />
                      Đang xử lý...
                    </>
                  ) : (
                    <>
                      <FaLock className="me-2" />
                      Hoàn tất đơn hàng
                    </>
                  )}
                </Button>

                <div className="text-center">
                  <small className="text-muted">
                    Bằng cách hoàn tất đơn hàng, bạn đồng ý với{' '}
                    <Link to="/terms" className="text-decoration-none">
                      Điều khoản sử dụng
                    </Link>
                  </small>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Form>

      {/* Success Modal */}
      <Modal show={showSuccessModal} onHide={() => setShowSuccessModal(false)} centered>
        <Modal.Body className="text-center p-5">
          <div className="mb-4">
            <FaCheckCircle className="text-success" style={{ fontSize: '4rem' }} />
          </div>
          <h4 className="mb-3">Đặt hàng thành công!</h4>
          <p className="text-muted mb-3">
            Cảm ơn bạn đã đặt hàng. Chúng tôi sẽ liên hệ với bạn trong thời gian sớm nhất.
          </p>
          <div className="bg-light p-3 rounded mb-4">
            <strong>Mã đơn hàng:</strong> {orderNumber}
          </div>
          <div className="d-grid gap-2">
            <Button variant="primary" onClick={() => navigate('/orders')}>
              Xem đơn hàng
            </Button>
            <Button variant="outline-secondary" onClick={() => navigate('/')}>
              Về trang chủ
            </Button>
          </div>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default CheckoutPage;
