import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button, Badge, Spinner, Alert, Form, InputGroup } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import { FaStar, FaShoppingCart, FaHeart, FaShare, FaArrowLeft } from 'react-icons/fa';
import { useBookController, useCartController } from '../../controllers';

const BookDetailPage = () => {
  const { id } = useParams();
  const { currentBook, loading, error, fetchBookById } = useBookController();
  const { addToCart, getQuantity } = useCartController();
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);

  useEffect(() => {
    if (id) {
      fetchBookById(id);
    }
  }, [id, fetchBookById]);

  const handleAddToCart = async () => {
    await addToCart(currentBook, quantity);
  };

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0 && value <= currentBook.stock) {
      setQuantity(value);
    }
  };

  if (loading) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </Spinner>
        </div>
      </Container>
    );
  }

  if (error || !currentBook) {
    return (
      <Container className="py-5">
        <Alert variant="danger">
          {error || 'Không tìm thấy sách'}
        </Alert>
        <Button variant="outline-primary" as={Link} to="/books">
          <FaArrowLeft className="me-2" />
          Quay lại danh sách sách
        </Button>
      </Container>
    );
  }

  const images = currentBook.image ? [currentBook.image] : ['/images/no-image.jpg'];

  return (
    <Container className="py-4">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/">Trang chủ</Link>
          </li>
          <li className="breadcrumb-item">
            <Link to="/books">Sách</Link>
          </li>
          <li className="breadcrumb-item active">{currentBook.title}</li>
        </ol>
      </nav>

      <Row>
        {/* Book Images */}
        <Col lg={5} className="mb-4">
          <Card className="border-0">
            <div className="text-center mb-3">
              <img
                src={images[selectedImage]}
                alt={currentBook.title}
                className="img-fluid rounded shadow"
                style={{ maxHeight: '500px', objectFit: 'contain' }}
              />
            </div>
            {images.length > 1 && (
              <div className="d-flex justify-content-center gap-2">
                {images.map((img, index) => (
                  <button
                    key={index}
                    className={`btn btn-outline-secondary btn-sm ${selectedImage === index ? 'active' : ''}`}
                    onClick={() => setSelectedImage(index)}
                    style={{ width: '60px', height: '60px', padding: 0 }}
                  >
                    <img
                      src={img}
                      alt={`${currentBook.title} ${index + 1}`}
                      className="img-fluid rounded"
                      style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                    />
                  </button>
                ))}
              </div>
            )}
          </Card>
        </Col>

        {/* Book Info */}
        <Col lg={7}>
          <div className="mb-4">
            <h1 className="h2 fw-bold mb-3">{currentBook.title}</h1>
            
            <div className="d-flex align-items-center mb-3">
              <div className="d-flex align-items-center me-3">
                <FaStar className="text-warning me-1" />
                <span className="fw-bold">{currentBook.getAverageRating()}</span>
                <span className="text-muted ms-1">(4.5/5)</span>
              </div>
              <Badge bg="secondary" className="me-3">
                {currentBook.category}
              </Badge>
              <span className="text-muted">Tác giả: {currentBook.author}</span>
            </div>

            <div className="h3 text-primary mb-3">
              {currentBook.getFormattedPrice()}
            </div>

            <div className="mb-4">
              <h5>Mô tả sản phẩm</h5>
              <p className="text-muted">{currentBook.description}</p>
            </div>

            {/* Stock Status */}
            <div className="mb-4">
              {currentBook.isInStock() ? (
                <Badge bg="success" className="fs-6">
                  Còn hàng ({currentBook.stock} cuốn)
                </Badge>
              ) : (
                <Badge bg="danger" className="fs-6">
                  Hết hàng
                </Badge>
              )}
            </div>

            {/* Quantity and Add to Cart */}
            {currentBook.isInStock() && (
              <div className="mb-4">
                <Row className="align-items-center">
                  <Col md={4}>
                    <Form.Label>Số lượng:</Form.Label>
                    <Form.Control
                      type="number"
                      min="1"
                      max={currentBook.stock}
                      value={quantity}
                      onChange={handleQuantityChange}
                      style={{ maxWidth: '100px' }}
                    />
                  </Col>
                  <Col md={8}>
                    <div className="d-flex gap-2">
                      <Button
                        variant="primary"
                        size="lg"
                        onClick={handleAddToCart}
                        className="flex-grow-1"
                      >
                        <FaShoppingCart className="me-2" />
                        Thêm vào giỏ hàng
                      </Button>
                      <Button variant="outline-danger" size="lg">
                        <FaHeart />
                      </Button>
                      <Button variant="outline-secondary" size="lg">
                        <FaShare />
                      </Button>
                    </div>
                  </Col>
                </Row>
              </div>
            )}

            {/* Book Details */}
            <Card className="mt-4">
              <Card.Header>
                <h5 className="mb-0">Thông tin chi tiết</h5>
              </Card.Header>
              <Card.Body>
                <Row>
                  <Col md={6}>
                    <p><strong>Tác giả:</strong> {currentBook.author}</p>
                    <p><strong>Danh mục:</strong> {currentBook.category}</p>
                  </Col>
                  <Col md={6}>
                    <p><strong>Số trang:</strong> 300 trang</p>
                    <p><strong>Ngôn ngữ:</strong> Tiếng Việt</p>
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          </div>
        </Col>
      </Row>

      {/* Reviews Section */}
      <Row className="mt-5">
        <Col>
          <h3>Đánh giá sản phẩm</h3>
          <Card>
            <Card.Body>
              <div className="text-center py-4">
                <div className="fs-1 mb-3">⭐</div>
                <h5>Chưa có đánh giá nào</h5>
                <p className="text-muted">Hãy là người đầu tiên đánh giá sản phẩm này</p>
                <Button variant="primary">Viết đánh giá</Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default BookDetailPage;
