import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button, Badge, Spinner, Alert, Form, InputGroup } from 'react-bootstrap';
import { Link, useSearchParams } from 'react-router-dom';
import { FaStar, FaShoppingCart, FaEye, FaSearch, FaFilter } from 'react-icons/fa';
import { useBookController, useCartController } from '../../controllers';

const BookCard = ({ book }) => {
  const { addToCart } = useCartController();

  const handleAddToCart = async () => {
    await addToCart(book, 1);
  };

  return (
    <Card className="h-100 book-card shadow-sm">
      <div className="position-relative">
        <Card.Img
          variant="top"
          src={book.image || '/images/no-image.jpg'}
          alt={book.title}
          style={{ height: '250px', objectFit: 'cover' }}
        />
        {!book.isInStock() && (
          <Badge bg="danger" className="position-absolute top-0 end-0 m-2">
            H·∫øt h√†ng
          </Badge>
        )}
      </div>
      <Card.Body className="d-flex flex-column">
        <Card.Title className="h6 mb-2" style={{ fontSize: '0.9rem' }}>
          {book.title}
        </Card.Title>
        <Card.Text className="text-muted small mb-2">
          T√°c gi·∫£: {book.author}
        </Card.Text>
        <div className="d-flex align-items-center mb-2">
          <div className="d-flex align-items-center me-2">
            <FaStar className="text-warning me-1" />
            <span className="small">{book.getAverageRating()}</span>
          </div>
          <Badge bg="secondary" className="small">
            {book.category}
          </Badge>
        </div>
        <div className="mt-auto">
          <div className="h5 text-primary mb-3">
            {book.getFormattedPrice()}
          </div>
          <div className="d-flex gap-2">
            <Button
              variant="outline-primary"
              size="sm"
              as={Link}
              to={`/books/${book.id}`}
              className="flex-grow-1"
            >
              <FaEye className="me-1" />
              Xem
            </Button>
            <Button
              variant="primary"
              size="sm"
              onClick={handleAddToCart}
              disabled={!book.isInStock()}
              className="flex-grow-1"
            >
              <FaShoppingCart className="me-1" />
              Mua
            </Button>
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

const BooksPage = () => {
  const { books, loading, error, fetchBooks, searchBooks, sortBooks } = useBookController();
  const [searchParams, setSearchParams] = useSearchParams();
  const [searchQuery, setSearchQuery] = useState(searchParams.get('q') || '');
  const [sortBy, setSortBy] = useState('title-asc');
  const [category, setCategory] = useState('');

  useEffect(() => {
    const loadBooks = async () => {
      const query = searchParams.get('q');
      if (query) {
        await searchBooks(query);
      } else {
        await fetchBooks();
      }
    };
    loadBooks();
  }, [searchParams, fetchBooks, searchBooks]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setSearchParams({ q: searchQuery.trim() });
    } else {
      setSearchParams({});
    }
  };

  const handleSortChange = (e) => {
    const newSortBy = e.target.value;
    setSortBy(newSortBy);
    sortBooks(newSortBy);
  };

  const categories = [
    'T·∫•t c·∫£',
    'VƒÉn h·ªçc',
    'Khoa h·ªçc',
    'L·ªãch s·ª≠',
    'Kinh t·∫ø',
    'T√¢m l√Ω h·ªçc',
    'C√¥ng ngh·ªá',
    'Ngh·ªá thu·∫≠t'
  ];

  return (
    <Container className="py-4">
      {/* Header */}
      <Row className="mb-4">
        <Col>
          <h1 className="h2 fw-bold">Danh s√°ch s√°ch</h1>
          <p className="text-muted">Kh√°m ph√° b·ªô s∆∞u t·∫≠p s√°ch ƒëa d·∫°ng c·ªßa ch√∫ng t√¥i</p>
        </Col>
      </Row>

      {/* Search and Filters */}
      <Row className="mb-4">
        <Col md={6}>
          <Form onSubmit={handleSearch}>
            <InputGroup>
              <Form.Control
                type="text"
                placeholder="T√¨m ki·∫øm s√°ch..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <Button variant="primary" type="submit">
                <FaSearch />
              </Button>
            </InputGroup>
          </Form>
        </Col>
        <Col md={3}>
          <Form.Select value={category} onChange={(e) => setCategory(e.target.value)}>
            {categories.map(cat => (
              <option key={cat} value={cat === 'T·∫•t c·∫£' ? '' : cat}>
                {cat}
              </option>
            ))}
          </Form.Select>
        </Col>
        <Col md={3}>
          <Form.Select value={sortBy} onChange={handleSortChange}>
            <option value="title-asc">T√™n A-Z</option>
            <option value="title-desc">T√™n Z-A</option>
            <option value="price-asc">Gi√° th·∫•p ƒë·∫øn cao</option>
            <option value="price-desc">Gi√° cao ƒë·∫øn th·∫•p</option>
            <option value="rating-desc">ƒê√°nh gi√° cao nh·∫•t</option>
          </Form.Select>
        </Col>
      </Row>

      {/* Results */}
      {loading ? (
        <div className="text-center py-5">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">ƒêang t·∫£i...</span>
          </Spinner>
        </div>
      ) : error ? (
        <Alert variant="danger">
          {error}
        </Alert>
      ) : (
        <>
          <Row className="mb-3">
            <Col>
              <p className="text-muted">
                T√¨m th·∫•y {books.length} cu·ªën s√°ch
                {searchParams.get('q') && ` cho "${searchParams.get('q')}"`}
              </p>
            </Col>
          </Row>

          {books.length > 0 ? (
            <Row>
              {books.map(book => (
                <Col lg={3} md={6} className="mb-4" key={book.id}>
                  <BookCard book={book} />
                </Col>
              ))}
            </Row>
          ) : (
            <Row>
              <Col className="text-center py-5">
                <div className="fs-1 mb-3">üìö</div>
                <h4>Kh√¥ng t√¨m th·∫•y s√°ch</h4>
                <p className="text-muted">H√£y th·ª≠ t√¨m ki·∫øm v·ªõi t·ª´ kh√≥a kh√°c</p>
                <Button variant="primary" onClick={() => {
                  setSearchQuery('');
                  setSearchParams({});
                }}>
                  Xem t·∫•t c·∫£ s√°ch
                </Button>
              </Col>
            </Row>
          )}
        </>
      )}
    </Container>
  );
};

