import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Spinner, Nav, Tab } from 'react-bootstrap';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { FaEye, FaEyeSlash, FaGoogle, FaFacebook } from 'react-icons/fa';

const AuthPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [activeTab, setActiveTab] = useState('login');
  
  // Set initial tab based on URL
  useEffect(() => {
    if (location.pathname === '/register') {
      setActiveTab('register');
    } else {
      setActiveTab('login');
    }
  }, [location.pathname]);
  
  // Login form state
  const [loginData, setLoginData] = useState({
    email: '',
    password: ''
  });
  
  // Register form state
  const [registerData, setRegisterData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    agreeTerms: false
  });
  
  const [showPassword, setShowPassword] = useState({
    login: false,
    register: false,
    confirm: false
  });
  
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [rememberMe, setRememberMe] = useState(false);

  const handleLoginChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({
      ...prev,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleRegisterChange = (e) => {
    const { name, value, type, checked } = e.target;
    setRegisterData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateLoginForm = () => {
    const newErrors = {};

    if (!loginData.email) {
      newErrors.email = 'Email là bắt buộc';
    } else if (!/\S+@\S+\.\S+/.test(loginData.email)) {
      newErrors.email = 'Email không hợp lệ';
    }

    if (!loginData.password) {
      newErrors.password = 'Mật khẩu là bắt buộc';
    } else if (loginData.password.length < 6) {
      newErrors.password = 'Mật khẩu phải có ít nhất 6 ký tự';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateRegisterForm = () => {
    const newErrors = {};

    if (!registerData.firstName) {
      newErrors.firstName = 'Họ là bắt buộc';
    }

    if (!registerData.lastName) {
      newErrors.lastName = 'Tên là bắt buộc';
    }

    if (!registerData.email) {
      newErrors.email = 'Email là bắt buộc';
    } else if (!/\S+@\S+\.\S+/.test(registerData.email)) {
      newErrors.email = 'Email không hợp lệ';
    }

    if (!registerData.password) {
      newErrors.password = 'Mật khẩu là bắt buộc';
    } else if (registerData.password.length < 8) {
      newErrors.password = 'Mật khẩu phải có ít nhất 8 ký tự';
    }

    if (!registerData.confirmPassword) {
      newErrors.confirmPassword = 'Xác nhận mật khẩu là bắt buộc';
    } else if (registerData.password !== registerData.confirmPassword) {
      newErrors.confirmPassword = 'Mật khẩu không khớp';
    }

    if (!registerData.phone) {
      newErrors.phone = 'Số điện thoại là bắt buộc';
    } else if (!/^[0-9]{10,11}$/.test(registerData.phone)) {
      newErrors.phone = 'Số điện thoại không hợp lệ';
    }

    if (!registerData.agreeTerms) {
      newErrors.agreeTerms = 'Bạn phải đồng ý với điều khoản sử dụng';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateLoginForm()) {
      return;
    }

    setLoading(true);
    
    // Simulate API call
    setTimeout(() => {
      localStorage.setItem('user', JSON.stringify({
        id: 1,
        email: loginData.email,
        name: 'Nguyễn Văn A',
        avatar: 'https://via.placeholder.com/100x100/6366f1/ffffff?text=User'
      }));
      
      setLoading(false);
      navigate('/');
    }, 1000);
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateRegisterForm()) {
      return;
    }

    setLoading(true);
    
    // Simulate API call
    setTimeout(() => {
      localStorage.setItem('user', JSON.stringify({
        id: 2,
        email: registerData.email,
        name: `${registerData.firstName} ${registerData.lastName}`,
        avatar: 'https://via.placeholder.com/100x100/10b981/ffffff?text=New'
      }));
      
      setLoading(false);
      navigate('/');
    }, 1000);
  };

  const handleSocialLogin = (provider) => {
    alert(`Đăng nhập với ${provider} (Chức năng sẽ được tích hợp sau)`);
  };

  const togglePasswordVisibility = (type) => {
    setShowPassword(prev => ({
      ...prev,
      [type]: !prev[type]
    }));
  };

  const getPasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 8) strength++;
    if (/[A-Z]/.test(password)) strength++;
    if (/[a-z]/.test(password)) strength++;
    if (/[0-9]/.test(password)) strength++;
    if (/[^A-Za-z0-9]/.test(password)) strength++;
    return strength;
  };

  const LoginForm = () => (
    <Form onSubmit={handleLoginSubmit}>
      <Form.Group className="mb-3">
        <Form.Label>Email</Form.Label>
        <Form.Control
          type="email"
          name="email"
          value={loginData.email}
          onChange={handleLoginChange}
          placeholder="Nhập email của bạn"
          isInvalid={!!errors.email}
        />
        <Form.Control.Feedback type="invalid">
          {errors.email}
        </Form.Control.Feedback>
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Mật khẩu</Form.Label>
        <div className="position-relative">
          <Form.Control
            type={showPassword.login ? 'text' : 'password'}
            name="password"
            value={loginData.password}
            onChange={handleLoginChange}
            placeholder="Nhập mật khẩu"
            isInvalid={!!errors.password}
          />
          <Button
            variant="link"
            className="position-absolute end-0 top-50 translate-middle-y pe-3"
            onClick={() => togglePasswordVisibility('login')}
            style={{ border: 'none', background: 'none' }}
          >
            {showPassword.login ? <FaEyeSlash /> : <FaEye />}
          </Button>
        </div>
        <Form.Control.Feedback type="invalid">
          {errors.password}
        </Form.Control.Feedback>
      </Form.Group>

      <div className="d-flex justify-content-between align-items-center mb-3">
        <Form.Check
          type="checkbox"
          label="Ghi nhớ đăng nhập"
          checked={rememberMe}
          onChange={(e) => setRememberMe(e.target.checked)}
        />
        <Link to="/forgot-password" className="text-decoration-none">
          Quên mật khẩu?
        </Link>
      </div>

      <Button
        type="submit"
        variant="primary"
        size="lg"
        className="w-100 mb-3"
        disabled={loading}
      >
        {loading ? (
          <>
            <Spinner size="sm" className="me-2" />
            Đang đăng nhập...
          </>
        ) : (
          'Đăng nhập'
        )}
      </Button>
    </Form>
  );

  const RegisterForm = () => (
    <Form onSubmit={handleRegisterSubmit}>
      <Row>
        <Col md={6}>
          <Form.Group className="mb-3">
            <Form.Label>Họ</Form.Label>
            <Form.Control
              type="text"
              name="firstName"
              value={registerData.firstName}
              onChange={handleRegisterChange}
              placeholder="Nhập họ"
              isInvalid={!!errors.firstName}
            />
            <Form.Control.Feedback type="invalid">
              {errors.firstName}
            </Form.Control.Feedback>
          </Form.Group>
        </Col>
        <Col md={6}>
          <Form.Group className="mb-3">
            <Form.Label>Tên</Form.Label>
            <Form.Control
              type="text"
              name="lastName"
              value={registerData.lastName}
              onChange={handleRegisterChange}
              placeholder="Nhập tên"
              isInvalid={!!errors.lastName}
            />
            <Form.Control.Feedback type="invalid">
              {errors.lastName}
            </Form.Control.Feedback>
          </Form.Group>
        </Col>
      </Row>

      <Form.Group className="mb-3">
        <Form.Label>Email</Form.Label>
        <Form.Control
          type="email"
          name="email"
          value={registerData.email}
          onChange={handleRegisterChange}
          placeholder="Nhập email của bạn"
          isInvalid={!!errors.email}
        />
        <Form.Control.Feedback type="invalid">
          {errors.email}
        </Form.Control.Feedback>
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Số điện thoại</Form.Label>
        <Form.Control
          type="tel"
          name="phone"
          value={registerData.phone}
          onChange={handleRegisterChange}
          placeholder="Nhập số điện thoại"
          isInvalid={!!errors.phone}
        />
        <Form.Control.Feedback type="invalid">
          {errors.phone}
        </Form.Control.Feedback>
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Mật khẩu</Form.Label>
        <div className="position-relative">
          <Form.Control
            type={showPassword.register ? 'text' : 'password'}
            name="password"
            value={registerData.password}
            onChange={handleRegisterChange}
            placeholder="Nhập mật khẩu"
            isInvalid={!!errors.password}
          />
          <Button
            variant="link"
            className="position-absolute end-0 top-50 translate-middle-y pe-3"
            onClick={() => togglePasswordVisibility('register')}
            style={{ border: 'none', background: 'none' }}
          >
            {showPassword.register ? <FaEyeSlash /> : <FaEye />}
          </Button>
        </div>
        {registerData.password && (
          <div className="mt-2">
            <div className="d-flex align-items-center">
              <span className="me-2 small">Độ mạnh:</span>
              <div className="flex-grow-1">
                <div className="progress" style={{ height: '4px' }}>
                  <div
                    className={`progress-bar ${
                      getPasswordStrength(registerData.password) <= 2 ? 'bg-danger' :
                      getPasswordStrength(registerData.password) <= 3 ? 'bg-warning' : 'bg-success'
                    }`}
                    style={{ width: `${(getPasswordStrength(registerData.password) / 5) * 100}%` }}
                  ></div>
                </div>
              </div>
            </div>
          </div>
        )}
        <Form.Control.Feedback type="invalid">
          {errors.password}
        </Form.Control.Feedback>
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Xác nhận mật khẩu</Form.Label>
        <div className="position-relative">
          <Form.Control
            type={showPassword.confirm ? 'text' : 'password'}
            name="confirmPassword"
            value={registerData.confirmPassword}
            onChange={handleRegisterChange}
            placeholder="Nhập lại mật khẩu"
            isInvalid={!!errors.confirmPassword}
          />
          <Button
            variant="link"
            className="position-absolute end-0 top-50 translate-middle-y pe-3"
            onClick={() => togglePasswordVisibility('confirm')}
            style={{ border: 'none', background: 'none' }}
          >
            {showPassword.confirm ? <FaEyeSlash /> : <FaEye />}
          </Button>
        </div>
        <Form.Control.Feedback type="invalid">
          {errors.confirmPassword}
        </Form.Control.Feedback>
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Check
          type="checkbox"
          name="agreeTerms"
          checked={registerData.agreeTerms}
          onChange={handleRegisterChange}
          label={
            <span>
              Tôi đồng ý với{' '}
              <Link to="/terms" className="text-decoration-none">
                Điều khoản sử dụng
              </Link>{' '}
              và{' '}
              <Link to="/privacy" className="text-decoration-none">
                Chính sách bảo mật
              </Link>
            </span>
          }
          isInvalid={!!errors.agreeTerms}
        />
        <Form.Control.Feedback type="invalid">
          {errors.agreeTerms}
        </Form.Control.Feedback>
      </Form.Group>

      <Button
        type="submit"
        variant="success"
        size="lg"
        className="w-100 mb-3"
        disabled={loading}
      >
        {loading ? (
          <>
            <Spinner size="sm" className="me-2" />
            Đang tạo tài khoản...
          </>
        ) : (
          'Tạo tài khoản'
        )}
      </Button>
    </Form>
  );

  return (
    <Container className="py-5">
      <Row className="justify-content-center">
        <Col md={8} lg={6}>
          <Card className="shadow">
            <Card.Body className="p-4">
              <div className="text-center mb-4">
                <h2 className="fw-bold">BookStore</h2>
                <p className="text-muted">
                  {activeTab === 'login' ? 'Chào mừng bạn quay trở lại!' : 'Tạo tài khoản mới'}
                </p>
              </div>

              <Tab.Container activeKey={activeTab} onSelect={setActiveTab}>
                <Nav variant="tabs" className="justify-content-center mb-4">
                  <Nav.Item>
                    <Nav.Link eventKey="login" className="fw-bold">
                      Đăng nhập
                    </Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link eventKey="register" className="fw-bold">
                      Đăng ký
                    </Nav.Link>
                  </Nav.Item>
                </Nav>

                <Tab.Content>
                  <Tab.Pane eventKey="login">
                    <LoginForm />
                  </Tab.Pane>
                  <Tab.Pane eventKey="register">
                    <RegisterForm />
                  </Tab.Pane>
                </Tab.Content>
              </Tab.Container>

              <div className="text-center mb-3">
                <span className="text-muted">Hoặc đăng nhập với</span>
              </div>

              <div className="d-grid gap-2 mb-3">
                <Button
                  variant="outline-danger"
                  onClick={() => handleSocialLogin('Google')}
                >
                  <FaGoogle className="me-2" />
                  Google
                </Button>
                <Button
                  variant="outline-primary"
                  onClick={() => handleSocialLogin('Facebook')}
                >
                  <FaFacebook className="me-2" />
                  Facebook
                </Button>
              </div>

              <div className="text-center">
                <span className="text-muted">
                  {activeTab === 'login' ? 'Chưa có tài khoản? ' : 'Đã có tài khoản? '}
                </span>
                <Button
                  variant="link"
                  className="text-decoration-none fw-bold p-0"
                  onClick={() => setActiveTab(activeTab === 'login' ? 'register' : 'login')}
                >
                  {activeTab === 'login' ? 'Đăng ký ngay' : 'Đăng nhập ngay'}
                </Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default AuthPage;
