import React, { useState } from 'react';
import { Button, Badge, Dropdown, Card, Row, Col, Alert } from 'react-bootstrap';
import { FaBell, FaTimes, FaCheck, FaExclamationTriangle, FaInfoCircle, FaTrash } from 'react-icons/fa';

const CartStyleNotification = () => {
  const [notifications, setNotifications] = useState([
    {
      id: 1,
      type: 'success',
      title: 'Đặt hàng thành công',
      message: 'Đơn hàng #12345 đã được xác nhận và đang chuẩn bị',
      time: '2 phút trước',
      read: false,
      category: 'Đơn hàng'
    },
    {
      id: 2,
      type: 'warning',
      title: 'Sản phẩm sắp hết hàng',
      message: 'Sách "React từ cơ bản" chỉ còn 3 cuốn trong kho',
      time: '1 giờ trước',
      read: false,
      category: 'Kho hàng'
    },
    {
      id: 3,
      type: 'info',
      title: 'Khuyến mãi mới',
      message: 'Giảm giá 20% cho tất cả sách công nghệ trong tuần này',
      time: '3 giờ trước',
      read: true,
      category: 'Khuyến mãi'
    },
    {
      id: 4,
      type: 'success',
      title: 'Thanh toán thành công',
      message: 'Giao dịch #67890 đã được xử lý thành công',
      time: '5 giờ trước',
      read: true,
      category: 'Thanh toán'
    }
  ]);

  const unreadCount = notifications.filter(n => !n.read).length;

  const markAsRead = (id) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === id 
          ? { ...notification, read: true }
          : notification
      )
    );
  };

  const deleteNotification = (id) => {
    setNotifications(prev => prev.filter(notification => notification.id !== id));
  };

  const markAllAsRead = () => {
    setNotifications(prev => 
      prev.map(notification => ({ ...notification, read: true }))
    );
  };

  const clearAllNotifications = () => {
    setNotifications([]);
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'success':
        return <FaCheck className="text-success" />;
      case 'warning':
        return <FaExclamationTriangle className="text-warning" />;
      case 'info':
        return <FaInfoCircle className="text-info" />;
      default:
        return <FaBell className="text-primary" />;
    }
  };

  const getCategoryBadgeColor = (category) => {
    switch (category) {
      case 'Đơn hàng':
        return 'primary';
      case 'Kho hàng':
        return 'warning';
      case 'Khuyến mãi':
        return 'success';
      case 'Thanh toán':
        return 'info';
      default:
        return 'secondary';
    }
  };

  return (
    <Dropdown align="end">
      <Dropdown.Toggle as={Button} variant="link" className="text-white position-relative">
        <FaBell />
        {unreadCount > 0 && (
          <Badge bg="danger" className="position-absolute top-0 end-0">
            {unreadCount}
          </Badge>
        )}
      </Dropdown.Toggle>

      <Dropdown.Menu style={{ width: '400px', maxHeight: '500px', overflowY: 'auto', padding: '0' }}>
        {/* Header */}
        <div className="p-3 border-bottom bg-light">
          <Row className="align-items-center">
            <Col>
              <h6 className="mb-0 fw-bold">Thông báo</h6>
              <small className="text-muted">{notifications.length} thông báo</small>
            </Col>
            <Col className="text-end">
              {unreadCount > 0 && (
                <Button 
                  variant="outline-primary" 
                  size="sm" 
                  onClick={markAllAsRead}
                  className="me-2"
                >
                  Đọc tất cả
                </Button>
              )}
              {notifications.length > 0 && (
                <Button 
                  variant="outline-danger" 
                  size="sm" 
                  onClick={clearAllNotifications}
                >
                  <FaTrash />
                </Button>
              )}
            </Col>
          </Row>
        </div>

        {/* Notifications List */}
        {notifications.length === 0 ? (
          <div className="text-center py-5">
            <div className="fs-1 mb-3">🔔</div>
            <h6>Không có thông báo</h6>
            <p className="text-muted small mb-0">Bạn chưa có thông báo nào</p>
          </div>
        ) : (
          <div className="p-0">
            {notifications.map(notification => (
              <div
                key={notification.id}
                className={`p-3 border-bottom ${!notification.read ? 'bg-light' : ''}`}
                style={{ cursor: 'pointer' }}
                onClick={() => markAsRead(notification.id)}
              >
                <Row className="align-items-start">
                  <Col xs={2} className="text-center">
                    <div className="mt-1">
                      {getNotificationIcon(notification.type)}
                    </div>
                  </Col>
                  <Col xs={8}>
                    <div className="d-flex align-items-center mb-1">
                      <h6 className={`mb-0 me-2 ${!notification.read ? 'fw-bold' : ''}`}>
                        {notification.title}
                      </h6>
                      <Badge bg={getCategoryBadgeColor(notification.category)} className="small">
                        {notification.category}
                      </Badge>
                    </div>
                    <p className="text-muted small mb-1">
                      {notification.message}
                    </p>
                    <small className="text-muted">{notification.time}</small>
                  </Col>
                  <Col xs={2} className="text-end">
                    <Button
                      variant="link"
                      size="sm"
                      className="text-muted p-0"
                      onClick={(e) => {
                        e.stopPropagation();
                        deleteNotification(notification.id);
                      }}
                    >
                      <FaTimes size={12} />
                    </Button>
                  </Col>
                </Row>
              </div>
            ))}
          </div>
        )}

        {/* Footer */}
        {notifications.length > 0 && (
          <div className="p-3 border-top bg-light text-center">
            <Button variant="link" size="sm" className="text-primary">
              Xem tất cả thông báo
            </Button>
          </div>
        )}
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default CartStyleNotification;
