import React, { useState, useEffect } from 'react';
import { ToastContainer, Toast, Button } from 'react-bootstrap';
import { FaBell, FaTimes, FaCheck, FaExclamationTriangle, FaInfoCircle } from 'react-icons/fa';

const NotificationSystem = () => {
  const [notifications, setNotifications] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);

  // Mock notifications data
  const mockNotifications = [
    {
      id: 1,
      type: 'success',
      title: 'Đặt hàng thành công',
      message: 'Đơn hàng #12345 của bạn đã được xác nhận và đang được chuẩn bị.',
      time: '2 phút trước',
      read: false,
      icon: FaCheck
    },
    {
      id: 2,
      type: 'warning',
      title: 'Sản phẩm sắp hết hàng',
      message: 'Sách "React từ cơ bản" chỉ còn 3 cuốn trong kho.',
      time: '1 giờ trước',
      read: false,
      icon: FaExclamationTriangle
    },
    {
      id: 3,
      type: 'info',
      title: 'Khuyến mãi mới',
      message: 'Giảm giá 20% cho tất cả sách công nghệ trong tuần này.',
      time: '3 giờ trước',
      read: true,
      icon: FaInfoCircle
    },
    {
      id: 4,
      type: 'success',
      title: 'Giao hàng thành công',
      message: 'Đơn hàng #12340 đã được giao đến địa chỉ của bạn.',
      time: '1 ngày trước',
      read: true,
      icon: FaCheck
    }
  ];

  useEffect(() => {
    setNotifications(mockNotifications);
  }, []);

  const unreadCount = notifications.filter(n => !n.read).length;

  const markAsRead = (id) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === id 
          ? { ...notification, read: true }
          : notification
      )
    );
  };

  const markAllAsRead = () => {
    setNotifications(prev => 
      prev.map(notification => ({ ...notification, read: true }))
    );
  };

  const deleteNotification = (id) => {
    setNotifications(prev => prev.filter(notification => notification.id !== id));
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'success':
        return <FaCheck className="text-success" />;
      case 'warning':
        return <FaExclamationTriangle className="text-warning" />;
      case 'info':
        return <FaInfoCircle className="text-info" />;
      default:
        return <FaBell className="text-primary" />;
    }
  };

  const getNotificationClass = (type) => {
    switch (type) {
      case 'success':
        return 'border-start border-success';
      case 'warning':
        return 'border-start border-warning';
      case 'info':
        return 'border-start border-info';
      default:
        return 'border-start border-primary';
    }
  };

  return (
    <>
      {/* Notification Bell */}
      <div className="position-relative">
        <Button
          variant="link"
          className="text-white position-relative"
          onClick={() => setShowDropdown(!showDropdown)}
        >
          <FaBell size={18} />
          {unreadCount > 0 && (
            <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
              {unreadCount}
            </span>
          )}
        </Button>

        {/* Notification Dropdown */}
        {showDropdown && (
          <div className="notification-dropdown">
            <div className="notification-header">
              <h6 className="mb-0">Thông báo</h6>
              {unreadCount > 0 && (
                <Button
                  variant="link"
                  size="sm"
                  className="text-primary p-0"
                  onClick={markAllAsRead}
                >
                  Đọc tất cả
                </Button>
              )}
            </div>

            <div className="notification-list">
              {notifications.length === 0 ? (
                <div className="text-center py-3 text-muted">
                  <FaBell className="mb-2" />
                  <p className="mb-0">Không có thông báo nào</p>
                </div>
              ) : (
                notifications.map(notification => (
                  <div
                    key={notification.id}
                    className={`notification-item ${getNotificationClass(notification.type)} ${
                      !notification.read ? 'bg-light' : ''
                    }`}
                    onClick={() => markAsRead(notification.id)}
                  >
                    <div className="notification-content">
                      <div className="d-flex align-items-start">
                        <div className="notification-icon me-2">
                          {getNotificationIcon(notification.type)}
                        </div>
                        <div className="flex-grow-1">
                          <h6 className={`mb-1 ${!notification.read ? 'fw-bold' : ''}`}>
                            {notification.title}
                          </h6>
                          <p className="mb-1 text-muted small">
                            {notification.message}
                          </p>
                          <small className="text-muted">{notification.time}</small>
                        </div>
                        <Button
                          variant="link"
                          size="sm"
                          className="text-muted p-0 ms-2"
                          onClick={(e) => {
                            e.stopPropagation();
                            deleteNotification(notification.id);
                          }}
                        >
                          <FaTimes size={12} />
                        </Button>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>

            <div className="notification-footer">
              <Button variant="link" size="sm" className="w-100 text-primary">
                Xem tất cả thông báo
              </Button>
            </div>
          </div>
        )}
      </div>

      {/* Toast Container for Real-time Notifications */}
      <ToastContainer
        position="top-end"
        className="p-3"
        style={{ zIndex: 1055 }}
      >
        {/* Toast notifications will be added here dynamically */}
      </ToastContainer>

      <style jsx>{`
        .notification-dropdown {
          position: absolute;
          top: 100%;
          right: 0;
          width: 350px;
          max-height: 400px;
          background: white;
          border: 1px solid #dee2e6;
          border-radius: 0.5rem;
          box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15);
          z-index: 1050;
          margin-top: 0.5rem;
        }

        .notification-header {
          padding: 1rem;
          border-bottom: 1px solid #dee2e6;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .notification-list {
          max-height: 300px;
          overflow-y: auto;
        }

        .notification-item {
          padding: 1rem;
          border-bottom: 1px solid #f8f9fa;
          cursor: pointer;
          transition: background-color 0.2s;
        }

        .notification-item:hover {
          background-color: #f8f9fa;
        }

        .notification-item:last-child {
          border-bottom: none;
        }

        .notification-content {
          width: 100%;
        }

        .notification-icon {
          flex-shrink: 0;
          width: 20px;
          text-align: center;
        }

        .notification-footer {
          padding: 0.75rem;
          border-top: 1px solid #dee2e6;
          text-align: center;
        }

        @media (max-width: 768px) {
          .notification-dropdown {
            width: 300px;
            right: -50px;
          }
        }

        @media (max-width: 480px) {
          .notification-dropdown {
            width: 280px;
            right: -80px;
          }
        }
      `}</style>
    </>
  );
};

export default NotificationSystem;
