import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Badge, Spinner, Alert, Table, Modal } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import { FaEye, FaDownload, FaMapMarkerAlt, FaPhone, FaEnvelope, FaCreditCard, FaTruck } from 'react-icons/fa';

const OrdersPage = () => {
  const { id } = useParams();
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showDetailModal, setShowDetailModal] = useState(false);

  // Mock orders data
  const mockOrders = [
    {
      id: 'ORD001',
      date: '2024-01-20',
      status: 'delivered',
      total: 950000,
      items: [
        {
          id: 1,
          book: {
            id: 1,
            title: "React.js Complete Guide",
            author: "Maximilian Schwarzmüller",
            price: 500000,
            image: "https://via.placeholder.com/300x400/6366f1/ffffff?text=React+Guide",
            category: "Công nghệ"
          },
          quantity: 1
        },
        {
          id: 2,
          book: {
            id: 2,
            title: "JavaScript ES6+ Mastery",
            author: "Kyle Simpson",
            price: 400000,
            image: "https://via.placeholder.com/300x400/ec4899/ffffff?text=JS+ES6",
            category: "Lập trình"
          },
          quantity: 1
        }
      ],
      shippingInfo: {
        fullName: 'Nguyễn Văn A',
        email: 'nguyenvana@email.com',
        phone: '0123456789',
        address: '123 Đường ABC, Quận 1',
        city: 'TP. Hồ Chí Minh',
        zipCode: '700000'
      },
      paymentMethod: 'cod',
      trackingNumber: 'VN123456789',
      notes: 'Giao hàng vào buổi chiều',
      timeline: [
        { status: 'ordered', date: '2024-01-20 10:00', description: 'Đơn hàng đã được đặt' },
        { status: 'confirmed', date: '2024-01-20 11:30', description: 'Đơn hàng đã được xác nhận' },
        { status: 'processing', date: '2024-01-20 14:00', description: 'Đang chuẩn bị hàng' },
        { status: 'shipping', date: '2024-01-21 08:00', description: 'Đã giao cho đơn vị vận chuyển' },
        { status: 'delivered', date: '2024-01-21 15:30', description: 'Đã giao hàng thành công' }
      ]
    },
    {
      id: 'ORD002',
      date: '2024-01-15',
      status: 'shipping',
      total: 750000,
      items: [
        {
          id: 3,
          book: {
            id: 3,
            title: "Node.js Backend Development",
            author: "Andrew Mead",
            price: 450000,
            image: "https://via.placeholder.com/300x400/10b981/ffffff?text=Node.js",
            category: "Backend"
          },
          quantity: 1
        },
        {
          id: 4,
          book: {
            id: 4,
            title: "CSS Grid & Flexbox",
            author: "Jen Simmons",
            price: 300000,
            image: "https://via.placeholder.com/300x400/f59e0b/ffffff?text=CSS+Grid",
            category: "Frontend"
          },
          quantity: 1
        }
      ],
      shippingInfo: {
        fullName: 'Nguyễn Văn A',
        email: 'nguyenvana@email.com',
        phone: '0123456789',
        address: '123 Đường ABC, Quận 1',
        city: 'TP. Hồ Chí Minh',
        zipCode: '700000'
      },
      paymentMethod: 'card',
      trackingNumber: 'VN987654321',
      notes: '',
      timeline: [
        { status: 'ordered', date: '2024-01-15 09:00', description: 'Đơn hàng đã được đặt' },
        { status: 'confirmed', date: '2024-01-15 10:30', description: 'Đơn hàng đã được xác nhận' },
        { status: 'processing', date: '2024-01-15 13:00', description: 'Đang chuẩn bị hàng' },
        { status: 'shipping', date: '2024-01-16 08:00', description: 'Đã giao cho đơn vị vận chuyển' }
      ]
    },
    {
      id: 'ORD003',
      date: '2024-01-10',
      status: 'processing',
      total: 500000,
      items: [
        {
          id: 5,
          book: {
            id: 5,
            title: "Python for Data Science",
            author: "Wes McKinney",
            price: 500000,
            image: "https://via.placeholder.com/300x400/8b5cf6/ffffff?text=Python+DS",
            category: "Data Science"
          },
          quantity: 1
        }
      ],
      shippingInfo: {
        fullName: 'Nguyễn Văn A',
        email: 'nguyenvana@email.com',
        phone: '0123456789',
        address: '123 Đường ABC, Quận 1',
        city: 'TP. Hồ Chí Minh',
        zipCode: '700000'
      },
      paymentMethod: 'cod',
      trackingNumber: null,
      notes: '',
      timeline: [
        { status: 'ordered', date: '2024-01-10 14:00', description: 'Đơn hàng đã được đặt' },
        { status: 'confirmed', date: '2024-01-10 15:30', description: 'Đơn hàng đã được xác nhận' },
        { status: 'processing', date: '2024-01-10 16:00', description: 'Đang chuẩn bị hàng' }
      ]
    }
  ];

  useEffect(() => {
    setLoading(true);
    // Simulate loading orders data
    setTimeout(() => {
      setOrders(mockOrders);
      setLoading(false);
    }, 500);
  }, []);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND'
    }).format(price);
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      'ordered': { variant: 'secondary', text: 'Đã đặt hàng' },
      'confirmed': { variant: 'info', text: 'Đã xác nhận' },
      'processing': { variant: 'warning', text: 'Đang xử lý' },
      'shipping': { variant: 'primary', text: 'Đang giao hàng' },
      'delivered': { variant: 'success', text: 'Đã giao hàng' },
      'cancelled': { variant: 'danger', text: 'Đã hủy' }
    };
    const statusInfo = statusMap[status] || { variant: 'secondary', text: status };
    return <Badge bg={statusInfo.variant}>{statusInfo.text}</Badge>;
  };

  const getPaymentMethodIcon = (method) => {
    return method === 'card' ? <FaCreditCard /> : <FaTruck />;
  };

  const getPaymentMethodText = (method) => {
    return method === 'card' ? 'Thẻ tín dụng' : 'Thanh toán khi nhận hàng';
  };

  const handleViewDetails = (order) => {
    setSelectedOrder(order);
    setShowDetailModal(true);
  };

  const OrderItem = ({ item }) => (
    <div className="d-flex align-items-center border-bottom py-3">
      <img
        src={item.book.image}
        alt={item.book.title}
        className="rounded me-3"
        style={{ width: '60px', height: '80px', objectFit: 'cover' }}
      />
      <div className="flex-grow-1">
        <h6 className="mb-1">{item.book.title}</h6>
        <p className="text-muted small mb-1">Tác giả: {item.book.author}</p>
        <Badge bg="secondary" className="small">
          {item.book.category}
        </Badge>
      </div>
      <div className="text-end">
        <div className="fw-bold">{formatPrice(item.book.price * item.quantity)}</div>
        <small className="text-muted">x{item.quantity}</small>
      </div>
    </div>
  );

  const TimelineItem = ({ timeline, index }) => {
    const isActive = index === timeline.length - 1;
    const isCompleted = index < timeline.length - 1;
    
    return (
      <div className="d-flex mb-3">
        <div className="flex-shrink-0 me-3">
          <div className={`rounded-circle d-flex align-items-center justify-content-center ${
            isActive ? 'bg-primary text-white' : 
            isCompleted ? 'bg-success text-white' : 'bg-light text-muted'
          }`} style={{ width: '40px', height: '40px' }}>
            {isCompleted ? '✓' : index + 1}
          </div>
        </div>
        <div className="flex-grow-1">
          <h6 className={`mb-1 ${isActive ? 'text-primary' : ''}`}>
            {timeline.description}
          </h6>
          <small className="text-muted">
            {new Date(timeline.date).toLocaleString('vi-VN')}
          </small>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </Spinner>
        </div>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      {/* Header */}
      <Row className="mb-4">
        <Col>
          <h1 className="h2 fw-bold">Lịch sử đơn hàng</h1>
          <p className="text-muted">Theo dõi và quản lý đơn hàng của bạn</p>
        </Col>
      </Row>

      {orders.length === 0 ? (
        <Row>
          <Col className="text-center py-5">
            <div className="fs-1 mb-3">📦</div>
            <h4>Chưa có đơn hàng nào</h4>
            <p className="text-muted mb-4">Bạn chưa có đơn hàng nào</p>
            <Button variant="primary" as={Link} to="/books">
              Mua sắm ngay
            </Button>
          </Col>
        </Row>
      ) : (
        <Row>
          <Col>
            <Card>
              <Card.Header>
                <h5 className="mb-0">Danh sách đơn hàng ({orders.length})</h5>
              </Card.Header>
              <Card.Body className="p-0">
                <div className="table-responsive">
                  <Table className="mb-0">
                    <thead className="table-light">
                      <tr>
                        <th>Mã đơn hàng</th>
                        <th>Ngày đặt</th>
                        <th>Sản phẩm</th>
                        <th>Tổng tiền</th>
                        <th>Trạng thái</th>
                        <th>Thao tác</th>
                      </tr>
                    </thead>
                    <tbody>
                      {orders.map(order => (
                        <tr key={order.id}>
                          <td>
                            <Link to={`/orders/${order.id}`} className="text-decoration-none fw-bold">
                              {order.id}
                            </Link>
                          </td>
                          <td>{new Date(order.date).toLocaleDateString('vi-VN')}</td>
                          <td>{order.items.length} sản phẩm</td>
                          <td className="fw-bold">{formatPrice(order.total)}</td>
                          <td>{getStatusBadge(order.status)}</td>
                          <td>
                            <div className="d-flex gap-2">
                              <Button 
                                variant="outline-primary" 
                                size="sm"
                                onClick={() => handleViewDetails(order)}
                              >
                                <FaEye className="me-1" />
                                Xem
                              </Button>
                              {order.status === 'delivered' && (
                                <Button variant="outline-success" size="sm">
                                  <FaDownload className="me-1" />
                                  Hóa đơn
                                </Button>
                              )}
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}

      {/* Order Detail Modal */}
      <Modal show={showDetailModal} onHide={() => setShowDetailModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Chi tiết đơn hàng {selectedOrder?.id}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedOrder && (
            <div>
              {/* Order Items */}
              <h6 className="mb-3">Sản phẩm đã đặt</h6>
              <div className="mb-4">
                {selectedOrder.items.map((item) => (
                  <OrderItem key={item.id} item={item} />
                ))}
              </div>

              {/* Order Summary */}
              <Row className="mb-4">
                <Col md={6}>
                  <Card>
                    <Card.Header>
                      <h6 className="mb-0">
                        <FaMapMarkerAlt className="me-2" />
                        Thông tin giao hàng
                      </h6>
                    </Card.Header>
                    <Card.Body>
                      <p className="mb-1"><strong>{selectedOrder.shippingInfo.fullName}</strong></p>
                      <p className="mb-1">
                        <FaPhone className="me-1" />
                        {selectedOrder.shippingInfo.phone}
                      </p>
                      <p className="mb-1">
                        <FaEnvelope className="me-1" />
                        {selectedOrder.shippingInfo.email}
                      </p>
                      <p className="mb-0">
                        {selectedOrder.shippingInfo.address}, {selectedOrder.shippingInfo.city}
                      </p>
                      {selectedOrder.shippingInfo.zipCode && (
                        <p className="mb-0">Mã bưu điện: {selectedOrder.shippingInfo.zipCode}</p>
                      )}
                    </Card.Body>
                  </Card>
                </Col>
                <Col md={6}>
                  <Card>
                    <Card.Header>
                      <h6 className="mb-0">
                        {getPaymentMethodIcon(selectedOrder.paymentMethod)} Phương thức thanh toán
                      </h6>
                    </Card.Header>
                    <Card.Body>
                      <p className="mb-1">{getPaymentMethodText(selectedOrder.paymentMethod)}</p>
                      <p className="mb-1"><strong>Tổng tiền:</strong> {formatPrice(selectedOrder.total)}</p>
                      {selectedOrder.trackingNumber && (
                        <p className="mb-0">
                          <strong>Mã vận đơn:</strong> {selectedOrder.trackingNumber}
                        </p>
                      )}
                    </Card.Body>
                  </Card>
                </Col>
              </Row>

              {/* Order Timeline */}
              <h6 className="mb-3">Trạng thái đơn hàng</h6>
              <div className="mb-3">
                {selectedOrder.timeline.map((timeline, index) => (
                  <TimelineItem key={index} timeline={timeline} index={index} />
                ))}
              </div>

              {selectedOrder.notes && (
                <div className="mt-3">
                  <h6>Ghi chú:</h6>
                  <p className="text-muted">{selectedOrder.notes}</p>
                </div>
              )}
            </div>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDetailModal(false)}>
            Đóng
          </Button>
          {selectedOrder?.status === 'delivered' && (
            <Button variant="primary">
              <FaDownload className="me-1" />
              Tải hóa đơn
            </Button>
          )}
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default OrdersPage;
