import React, { useState } from 'react';
import { Button, Badge, Dropdown } from 'react-bootstrap';
import { FaBell, FaTimes, FaCheck, FaExclamationTriangle, FaInfoCircle } from 'react-icons/fa';

const SimpleNotification = () => {
  const [notifications, setNotifications] = useState([
    {
      id: 1,
      type: 'success',
      title: 'Đặt hàng thành công',
      message: 'Đơn hàng #12345 đã được xác nhận',
      time: '2 phút trước',
      read: false
    },
    {
      id: 2,
      type: 'warning',
      title: 'Sản phẩm sắp hết hàng',
      message: 'Sách "React từ cơ bản" chỉ còn 3 cuốn',
      time: '1 giờ trước',
      read: false
    },
    {
      id: 3,
      type: 'info',
      title: 'Khuyến mãi mới',
      message: 'Giảm giá 20% cho tất cả sách công nghệ',
      time: '3 giờ trước',
      read: true
    }
  ]);

  const unreadCount = notifications.filter(n => !n.read).length;

  const markAsRead = (id) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === id 
          ? { ...notification, read: true }
          : notification
      )
    );
  };

  const deleteNotification = (id) => {
    setNotifications(prev => prev.filter(notification => notification.id !== id));
  };

  const markAllAsRead = () => {
    setNotifications(prev => 
      prev.map(notification => ({ ...notification, read: true }))
    );
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'success':
        return <FaCheck className="text-success" />;
      case 'warning':
        return <FaExclamationTriangle className="text-warning" />;
      case 'info':
        return <FaInfoCircle className="text-info" />;
      default:
        return <FaBell className="text-primary" />;
    }
  };

  return (
    <Dropdown align="end">
      <Dropdown.Toggle as={Button} variant="link" className="text-white position-relative">
        <FaBell />
        {unreadCount > 0 && (
          <Badge bg="danger" className="position-absolute top-0 end-0">
            {unreadCount}
          </Badge>
        )}
      </Dropdown.Toggle>

      <Dropdown.Menu style={{ width: '300px', maxHeight: '400px', overflowY: 'auto' }}>
        <div className="px-3 py-2 border-bottom">
          <div className="d-flex justify-content-between align-items-center">
            <h6 className="mb-0">Thông báo</h6>
            {unreadCount > 0 && (
              <Button 
                variant="link" 
                size="sm" 
                className="text-primary p-0"
                onClick={markAllAsRead}
              >
                Đọc tất cả
              </Button>
            )}
          </div>
        </div>

        {notifications.length === 0 ? (
          <div className="text-center py-3 text-muted">
            <FaBell className="mb-2" />
            <p className="mb-0">Không có thông báo nào</p>
          </div>
        ) : (
          notifications.map(notification => (
            <div
              key={notification.id}
              className={`px-3 py-2 border-bottom ${!notification.read ? 'bg-light' : ''}`}
              style={{ cursor: 'pointer' }}
              onClick={() => markAsRead(notification.id)}
            >
              <div className="d-flex align-items-start">
                <div className="me-2 mt-1">
                  {getNotificationIcon(notification.type)}
                </div>
                <div className="flex-grow-1">
                  <h6 className={`mb-1 ${!notification.read ? 'fw-bold' : ''}`}>
                    {notification.title}
                  </h6>
                  <p className="mb-1 text-muted small">
                    {notification.message}
                  </p>
                  <small className="text-muted">{notification.time}</small>
                </div>
                <Button
                  variant="link"
                  size="sm"
                  className="text-muted p-0 ms-2"
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteNotification(notification.id);
                  }}
                >
                  <FaTimes size={12} />
                </Button>
              </div>
            </div>
          ))
        )}

        <div className="text-center py-2">
          <Button variant="link" size="sm" className="text-primary">
            Xem tất cả thông báo
          </Button>
        </div>
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default SimpleNotification;
