import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Badge, Spinner, Alert, Form, InputGroup, Tab, Tabs } from 'react-bootstrap';
import { Link, useParams, useNavigate } from 'react-router-dom';
import { FaStar, FaShoppingCart, FaHeart, FaShare, FaArrowLeft, FaMinus, FaPlus, FaEye } from 'react-icons/fa';

const BookDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);
  const [activeTab, setActiveTab] = useState('description');
  const [isInWishlist, setIsInWishlist] = useState(false);

  // Mock data for books
  const mockBooks = [
    {
      id: 1,
      title: "React.js Complete Guide",
      author: "Maximilian Schwarzmüller",
      price: 500000,
      originalPrice: 600000,
      discount: 17,
      image: "https://via.placeholder.com/400x600/6366f1/ffffff?text=React+Guide",
      images: [
        "https://via.placeholder.com/400x600/6366f1/ffffff?text=React+Guide",
        "https://via.placeholder.com/400x600/8b5cf6/ffffff?text=React+Guide+2",
        "https://via.placeholder.com/400x600/ec4899/ffffff?text=React+Guide+3"
      ],
      category: "Công nghệ",
      stock: 10,
      rating: 4.8,
      reviews: 124,
      pages: 450,
      language: "Tiếng Việt",
      publisher: "NXB Công nghệ",
      publishDate: "2024-01-15",
      isbn: "978-1234567890",
      description: "Hướng dẫn toàn diện về React.js từ cơ bản đến nâng cao. Cuốn sách này sẽ giúp bạn nắm vững các khái niệm cốt lõi của React, bao gồm components, state management, hooks, và nhiều tính năng mới nhất.",
      detailedDescription: `
        <h4>Nội dung chính của cuốn sách:</h4>
        <ul>
          <li><strong>Chương 1-3:</strong> Giới thiệu React và thiết lập môi trường phát triển</li>
          <li><strong>Chương 4-6:</strong> Components và JSX cơ bản</li>
          <li><strong>Chương 7-9:</strong> State và Props management</li>
          <li><strong>Chương 10-12:</strong> React Hooks (useState, useEffect, useContext)</li>
          <li><strong>Chương 13-15:</strong> Routing với React Router</li>
          <li><strong>Chương 16-18:</strong> State management với Redux</li>
          <li><strong>Chương 19-21:</strong> Testing với Jest và React Testing Library</li>
          <li><strong>Chương 22-24:</strong> Performance optimization và deployment</li>
        </ul>
        
        <h4>Đối tượng độc giả:</h4>
        <p>Cuốn sách phù hợp cho:</p>
        <ul>
          <li>Developers mới bắt đầu với React</li>
          <li>Frontend developers muốn nâng cao kỹ năng</li>
          <li>Full-stack developers chuyển sang React</li>
          <li>Sinh viên ngành Công nghệ thông tin</li>
        </ul>
      `,
      specifications: {
        "Kích thước": "16 x 24 cm",
        "Số trang": "450 trang",
        "Loại bìa": "Bìa mềm",
        "Ngôn ngữ": "Tiếng Việt",
        "Nhà xuất bản": "NXB Công nghệ",
        "Ngày phát hành": "15/01/2024",
        "ISBN": "978-1234567890",
        "Trọng lượng": "650g"
      },
      reviews: [
        {
          id: 1,
          user: "Nguyễn Văn A",
          rating: 5,
          date: "2024-01-20",
          comment: "Cuốn sách rất hay và chi tiết. Tôi đã học được rất nhiều từ cuốn sách này."
        },
        {
          id: 2,
          user: "Trần Thị B",
          rating: 4,
          date: "2024-01-18",
          comment: "Nội dung tốt nhưng có một số phần hơi khó hiểu với người mới bắt đầu."
        },
        {
          id: 3,
          user: "Lê Văn C",
          rating: 5,
          date: "2024-01-15",
          comment: "Tuyệt vời! Tác giả giải thích rất rõ ràng và có nhiều ví dụ thực tế."
        }
      ],
      relatedBooks: [
        {
          id: 2,
          title: "JavaScript ES6+ Mastery",
          author: "Kyle Simpson",
          price: 400000,
          image: "https://via.placeholder.com/200x300/ec4899/ffffff?text=JS+ES6",
          rating: 4.9
        },
        {
          id: 3,
          title: "Node.js Backend Development",
          author: "Andrew Mead",
          price: 450000,
          image: "https://via.placeholder.com/200x300/10b981/ffffff?text=Node.js",
          rating: 4.7
        },
        {
          id: 4,
          title: "CSS Grid & Flexbox",
          author: "Jen Simmons",
          price: 350000,
          image: "https://via.placeholder.com/200x300/f59e0b/ffffff?text=CSS+Grid",
          rating: 4.6
        }
      ]
    }
  ];

  useEffect(() => {
    setLoading(true);
    // Simulate API call
    setTimeout(() => {
      const foundBook = mockBooks.find(b => b.id === parseInt(id));
      if (foundBook) {
        setBook(foundBook);
      }
      setLoading(false);
    }, 500);
  }, [id]);

  const handleQuantityChange = (change) => {
    const newQuantity = quantity + change;
    if (newQuantity >= 1 && newQuantity <= book.stock) {
      setQuantity(newQuantity);
    }
  };

  const handleAddToCart = () => {
    // Add to cart logic
    alert(`Đã thêm ${quantity} cuốn "${book.title}" vào giỏ hàng!`);
  };

  const handleBuyNow = () => {
    // Buy now logic
    navigate('/checkout');
  };

  const handleAddToWishlist = () => {
    setIsInWishlist(!isInWishlist);
    alert(isInWishlist ? 'Đã xóa khỏi danh sách yêu thích' : 'Đã thêm vào danh sách yêu thích');
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: book.title,
        text: book.description,
        url: window.location.href
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      alert('Đã copy link vào clipboard!');
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND'
    }).format(price);
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, index) => (
      <FaStar
        key={index}
        className={index < Math.floor(rating) ? 'text-warning' : 'text-muted'}
      />
    ));
  };

  if (loading) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </Spinner>
        </div>
      </Container>
    );
  }

  if (!book) {
    return (
      <Container className="py-5">
        <Alert variant="danger">
          <Alert.Heading>Không tìm thấy sách</Alert.Heading>
          <p>Sách bạn đang tìm kiếm không tồn tại hoặc đã bị xóa.</p>
          <hr />
          <div className="d-flex justify-content-end">
            <Button variant="primary" as={Link} to="/books">
              <FaArrowLeft className="me-2" />
              Quay lại danh sách sách
            </Button>
          </div>
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/">Trang chủ</Link>
          </li>
          <li className="breadcrumb-item">
            <Link to="/books">Sách</Link>
          </li>
          <li className="breadcrumb-item active">{book.title}</li>
        </ol>
      </nav>

      <Row>
        {/* Book Images */}
        <Col lg={5} className="mb-4">
          <Card className="border-0">
            <div className="text-center mb-3">
              <img
                src={book.images[selectedImage]}
                alt={book.title}
                className="img-fluid rounded shadow"
                style={{ maxHeight: '500px', objectFit: 'contain' }}
              />
            </div>
            {book.images.length > 1 && (
              <div className="d-flex justify-content-center gap-2">
                {book.images.map((img, index) => (
                  <button
                    key={index}
                    className={`btn btn-outline-secondary btn-sm ${selectedImage === index ? 'active' : ''}`}
                    onClick={() => setSelectedImage(index)}
                    style={{ width: '60px', height: '60px', padding: 0 }}
                  >
                    <img
                      src={img}
                      alt={`${book.title} ${index + 1}`}
                      className="img-fluid rounded"
                      style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                    />
                  </button>
                ))}
              </div>
            )}
          </Card>
        </Col>

        {/* Book Info */}
        <Col lg={7}>
          <div className="mb-4">
            <h1 className="h2 fw-bold mb-3">{book.title}</h1>
            
            <div className="d-flex align-items-center mb-3">
              <div className="d-flex align-items-center me-3">
                {renderStars(book.rating)}
                <span className="ms-2 fw-bold">{book.rating}</span>
                <span className="text-muted ms-1">({book.reviews} đánh giá)</span>
              </div>
              <Badge bg="secondary" className="me-3">
                {book.category}
              </Badge>
              <span className="text-muted">Tác giả: {book.author}</span>
            </div>

            <div className="mb-3">
              <div className="d-flex align-items-center">
                <span className="h3 text-primary me-3">
                  {formatPrice(book.price)}
                </span>
                {book.originalPrice && (
                  <>
                    <span className="text-muted text-decoration-line-through me-2">
                      {formatPrice(book.originalPrice)}
                    </span>
                    <Badge bg="danger">
                      -{book.discount}%
                    </Badge>
                  </>
                )}
              </div>
            </div>

            <div className="mb-4">
              <p className="text-muted">{book.description}</p>
            </div>

            {/* Stock Status */}
            <div className="mb-4">
              {book.stock > 0 ? (
                <Badge bg="success" className="fs-6">
                  Còn hàng ({book.stock} cuốn)
                </Badge>
              ) : (
                <Badge bg="danger" className="fs-6">
                  Hết hàng
                </Badge>
              )}
            </div>

            {/* Quantity and Actions */}
            {book.stock > 0 && (
              <div className="mb-4">
                <Row className="align-items-center">
                  <Col md={4}>
                    <Form.Label>Số lượng:</Form.Label>
                    <div className="d-flex align-items-center">
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={() => handleQuantityChange(-1)}
                        disabled={quantity <= 1}
                      >
                        <FaMinus />
                      </Button>
                      <Form.Control
                        type="number"
                        min="1"
                        max={book.stock}
                        value={quantity}
                        onChange={(e) => setQuantity(parseInt(e.target.value) || 1)}
                        className="mx-2 text-center"
                        style={{ maxWidth: '80px' }}
                      />
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={() => handleQuantityChange(1)}
                        disabled={quantity >= book.stock}
                      >
                        <FaPlus />
                      </Button>
                    </div>
                  </Col>
                  <Col md={8}>
                    <div className="d-flex gap-2">
                      <Button
                        variant="primary"
                        size="lg"
                        onClick={handleAddToCart}
                        className="flex-grow-1"
                      >
                        <FaShoppingCart className="me-2" />
                        Thêm vào giỏ hàng
                      </Button>
                      <Button
                        variant="success"
                        size="lg"
                        onClick={handleBuyNow}
                        className="flex-grow-1"
                      >
                        Mua ngay
                      </Button>
                    </div>
                    <div className="d-flex gap-2 mt-2">
                      <Button
                        variant={isInWishlist ? "danger" : "outline-danger"}
                        size="sm"
                        onClick={handleAddToWishlist}
                        className="flex-grow-1"
                      >
                        <FaHeart className="me-1" />
                        {isInWishlist ? 'Đã yêu thích' : 'Yêu thích'}
                      </Button>
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={handleShare}
                        className="flex-grow-1"
                      >
                        <FaShare className="me-1" />
                        Chia sẻ
                      </Button>
                    </div>
                  </Col>
                </Row>
              </div>
            )}

            {/* Book Details */}
            <Card className="mt-4">
              <Card.Header>
                <h5 className="mb-0">Thông tin chi tiết</h5>
              </Card.Header>
              <Card.Body>
                <Row>
                  {Object.entries(book.specifications).map(([key, value]) => (
                    <Col md={6} key={key}>
                      <p><strong>{key}:</strong> {value}</p>
                    </Col>
                  ))}
                </Row>
              </Card.Body>
            </Card>
          </div>
        </Col>
      </Row>

      {/* Tabs Section */}
      <Row className="mt-5">
        <Col>
          <Tabs
            activeKey={activeTab}
            onSelect={(k) => setActiveTab(k)}
            className="mb-3"
          >
            <Tab eventKey="description" title="Mô tả chi tiết">
              <Card>
                <Card.Body>
                  <div dangerouslySetInnerHTML={{ __html: book.detailedDescription }} />
                </Card.Body>
              </Card>
            </Tab>
            <Tab eventKey="reviews" title={`Đánh giá (${book.reviews})`}>
              <Card>
                <Card.Body>
                  {book.reviews && book.reviews.length > 0 ? (
                    <div>
                      {book.reviews.map(review => (
                        <div key={review.id} className="border-bottom pb-3 mb-3">
                          <div className="d-flex justify-content-between align-items-start mb-2">
                            <div>
                              <h6 className="mb-1">{review.user}</h6>
                              <div className="d-flex align-items-center">
                                {renderStars(review.rating)}
                                <span className="ms-2 text-muted small">{review.date}</span>
                              </div>
                            </div>
                          </div>
                          <p className="mb-0">{review.comment}</p>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-4">
                      <p className="text-muted">Chưa có đánh giá nào</p>
                    </div>
                  )}
                </Card.Body>
              </Card>
            </Tab>
          </Tabs>
        </Col>
      </Row>
    </Container>
  );
};

export default BookDetailPage;
