import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Navbar, Nav, Container, Badge, Dropdown, Form, InputGroup, Button } from 'react-bootstrap';
import { FaShoppingCart, FaUser, FaSearch, FaBars, FaBookOpen, FaBell } from 'react-icons/fa';
import NotificationBell from '../NotificationBell';

const Header = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [cartCount] = useState(3); // Mock cart count
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Auth state
  const [user, setUser] = useState(null); // User state

  useEffect(() => {
    // Check if user is logged in from localStorage
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      const userData = JSON.parse(savedUser);
      setUser(userData);
      setIsAuthenticated(true);
    }
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/books?q=${encodeURIComponent(searchQuery.trim())}`);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
    navigate('/');
  };

  return (
    <Navbar expand="lg" className="navbar-premium">
      <Container fluid className="px-4">
        {/* Logo và Search Bar */}
        <div className="d-flex align-items-center flex-grow-1">
          {/* Logo */}
          <Navbar.Brand as={Link} to="/" className="navbar-brand-premium me-4">
            <div className="brand-container">
              <FaBookOpen className="brand-icon" />
              <div className="brand-text">
                <span className="brand-name">BookStore</span>
                <span className="brand-tagline">Premium</span>
              </div>
            </div>
          </Navbar.Brand>

          {/* Search Bar - Desktop */}
          <div className="search-container d-none d-lg-flex">
            <Form onSubmit={handleSearch} className="search-form">
              <InputGroup className="search-input-group">
                <Form.Control
                  type="search"
                  placeholder="Tìm kiếm sách, tác giả, thể loại..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="search-input"
                />
                <Button type="submit" className="search-btn">
                  <FaSearch />
                </Button>
              </InputGroup>
            </Form>
          </div>
        </div>

        {/* Desktop Navigation */}
        <div className="d-none d-lg-flex align-items-center">
          <Nav className="navbar-nav-premium me-4">
            <Nav.Link as={Link} to="/" className="nav-link-premium">
              Trang chủ
            </Nav.Link>
            <Nav.Link as={Link} to="/books" className="nav-link-premium">
              Sách
            </Nav.Link>
            <Nav.Link as={Link} to="/categories" className="nav-link-premium">
              Danh mục
            </Nav.Link>
            <Nav.Link as={Link} to="/about" className="nav-link-premium">
              Giới thiệu
            </Nav.Link>
          </Nav>

          {/* User Actions */}
          <div className="user-actions d-flex align-items-center">
            <Link to="/cart" className="action-btn cart-btn me-3">
              <FaShoppingCart />
              {cartCount > 0 && (
                <Badge className="cart-badge-premium">{cartCount}</Badge>
              )}
            </Link>

            <NotificationBell />

            {isAuthenticated ? (
              <Dropdown align="end" className="ms-3">
                <Dropdown.Toggle as={Button} variant="link" className="user-dropdown-btn">
                  <FaUser className="me-2" />
                  {user?.fullName || user?.firstName || 'User'}
                </Dropdown.Toggle>
                <Dropdown.Menu className="user-dropdown-menu">
                  <Dropdown.Item as={Link} to="/profile">
                    <FaUser className="me-2" />
                    Thông tin cá nhân
                  </Dropdown.Item>
                  <Dropdown.Item as={Link} to="/orders">
                    <FaBookOpen className="me-2" />
                    Đơn hàng của tôi
                  </Dropdown.Item>
                  <Dropdown.Divider />
                  <Dropdown.Item onClick={handleLogout}>
                    Đăng xuất
                  </Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            ) : (
              <Button as={Link} to="/login" variant="outline-light" className="login-btn ms-3">
                Đăng nhập
              </Button>
            )}
          </div>
        </div>

        {/* Mobile Toggle */}
        <Navbar.Toggle aria-controls="premium-navbar-nav" className="mobile-toggle">
          <FaBars />
        </Navbar.Toggle>

        {/* Mobile Menu */}
        <Navbar.Collapse id="premium-navbar-nav" className="mobile-menu">
          {/* Mobile Search */}
          <div className="mobile-search mb-4">
            <Form onSubmit={handleSearch}>
              <InputGroup>
                <Form.Control
                  type="search"
                  placeholder="Tìm kiếm sách..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="mobile-search-input"
                />
                <Button type="submit" className="mobile-search-btn">
                  <FaSearch />
                </Button>
              </InputGroup>
            </Form>
          </div>

          {/* Mobile Navigation */}
          <Nav className="mobile-nav">
            <Nav.Link as={Link} to="/" className="mobile-nav-link">
              Trang chủ
            </Nav.Link>
            <Nav.Link as={Link} to="/books" className="mobile-nav-link">
              Sách
            </Nav.Link>
            <Nav.Link as={Link} to="/categories" className="mobile-nav-link">
              Danh mục
            </Nav.Link>
            <Nav.Link as={Link} to="/about" className="mobile-nav-link">
              Giới thiệu
            </Nav.Link>
          </Nav>

          {/* Mobile User Actions */}
          <div className="mobile-user-actions">
            <Link to="/cart" className="mobile-action-btn">
              <FaShoppingCart />
              <span>Giỏ hàng</span>
              {cartCount > 0 && (
                <Badge className="mobile-cart-badge">{cartCount}</Badge>
              )}
            </Link>

            <NotificationBell />

            {isAuthenticated ? (
              <div className="mobile-user-menu">
                <div className="user-info">
                  <FaUser />
                  <span>{user?.fullName || user?.firstName || 'User'}</span>
                </div>
                <div className="user-menu-links">
                  <Link to="/profile" className="user-menu-link">
                    Thông tin cá nhân
                  </Link>
                  <Link to="/orders" className="user-menu-link">
                    Đơn hàng của tôi
                  </Link>
                  <button onClick={handleLogout} className="user-menu-link logout-btn">
                    Đăng xuất
                  </button>
                </div>
              </div>
            ) : (
              <Link to="/login" className="mobile-login-btn">
                Đăng nhập
              </Link>
            )}
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
