import React, { useEffect } from 'react';
import { Container, Row, Col, Card, Button, Badge, Spinner, Alert, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { FaTrash, FaShoppingCart, FaArrowLeft, FaMinus, FaPlus } from 'react-icons/fa';
import { useCartController } from '../../controllers';

const CartPage = () => {
  const { 
    cart, 
    loading, 
    error, 
    updateQuantity, 
    removeFromCart, 
    clearCart,
    calculateTotalWithTaxAndShipping,
    validateCart 
  } = useCartController();

  const handleQuantityChange = async (bookId, newQuantity) => {
    await updateQuantity(bookId, newQuantity);
  };

  const handleRemoveItem = async (bookId) => {
    await removeFromCart(bookId);
  };

  const handleClearCart = async () => {
    if (window.confirm('Bạn có chắc chắn muốn xóa tất cả sách khỏi giỏ hàng?')) {
      await clearCart();
    }
  };

  const totals = calculateTotalWithTaxAndShipping();

  const validation = validateCart();

  if (loading) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </Spinner>
        </div>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      {/* Header */}
      <Row className="mb-4">
        <Col>
          <h1 className="h2 fw-bold">Giỏ hàng</h1>
          <p className="text-muted">Kiểm tra và chỉnh sửa giỏ hàng của bạn</p>
        </Col>
      </Row>

      {error && (
        <Alert variant="danger" className="mb-4">
          {error}
        </Alert>
      )}

      {cart.isEmpty() ? (
        <Row>
          <Col className="text-center py-5">
            <div className="fs-1 mb-3">🛒</div>
            <h4>Giỏ hàng trống</h4>
            <p className="text-muted mb-4">Bạn chưa có sản phẩm nào trong giỏ hàng</p>
            <Button variant="primary" as={Link} to="/books">
              <FaShoppingCart className="me-2" />
              Tiếp tục mua sắm
            </Button>
          </Col>
        </Row>
      ) : (
        <Row>
          {/* Cart Items */}
          <Col lg={8}>
            <Card>
              <Card.Header className="d-flex justify-content-between align-items-center">
                <h5 className="mb-0">Sản phẩm ({cart.getTotalItems()})</h5>
                <Button variant="outline-danger" size="sm" onClick={handleClearCart}>
                  <FaTrash className="me-1" />
                  Xóa tất cả
                </Button>
              </Card.Header>
              <Card.Body className="p-0">
                {cart.items.map((item) => (
                  <div key={item.book.id} className="border-bottom p-3">
                    <Row className="align-items-center">
                      <Col md={2}>
                        <img
                          src={item.book.image || '/images/no-image.jpg'}
                          alt={item.book.title}
                          className="img-fluid rounded"
                          style={{ height: '80px', objectFit: 'cover' }}
                        />
                      </Col>
                      <Col md={4}>
                        <h6 className="mb-1">{item.book.title}</h6>
                        <p className="text-muted small mb-0">Tác giả: {item.book.author}</p>
                        <Badge bg="secondary" className="small">
                          {item.book.category}
                        </Badge>
                      </Col>
                      <Col md={2}>
                        <div className="text-primary fw-bold">
                          {item.book.getFormattedPrice()}
                        </div>
                      </Col>
                      <Col md={2}>
                        <div className="d-flex align-items-center">
                          <Button
                            variant="outline-secondary"
                            size="sm"
                            onClick={() => handleQuantityChange(item.book.id, item.quantity - 1)}
                            disabled={item.quantity <= 1}
                          >
                            <FaMinus />
                          </Button>
                          <Form.Control
                            type="number"
                            min="1"
                            max={item.book.stock}
                            value={item.quantity}
                            onChange={(e) => handleQuantityChange(item.book.id, parseInt(e.target.value))}
                            className="mx-2 text-center"
                            style={{ width: '60px' }}
                          />
                          <Button
                            variant="outline-secondary"
                            size="sm"
                            onClick={() => handleQuantityChange(item.book.id, item.quantity + 1)}
                            disabled={item.quantity >= item.book.stock}
                          >
                            <FaPlus />
                          </Button>
                        </div>
                      </Col>
                      <Col md={2}>
                        <div className="text-end">
                          <div className="fw-bold text-primary mb-2">
                            {(item.book.price * item.quantity).toLocaleString('vi-VN', {
                              style: 'currency',
                              currency: 'VND'
                            })}
                          </div>
                          <Button
                            variant="outline-danger"
                            size="sm"
                            onClick={() => handleRemoveItem(item.book.id)}
                          >
                            <FaTrash />
                          </Button>
                        </div>
                      </Col>
                    </Row>
                  </div>
                ))}
              </Card.Body>
            </Card>
          </Col>

          {/* Order Summary */}
          <Col lg={4}>
            <Card className="sticky-top" style={{ top: '20px' }}>
              <Card.Header>
                <h5 className="mb-0">Tóm tắt đơn hàng</h5>
              </Card.Header>
              <Card.Body>
                <div className="d-flex justify-content-between mb-2">
                  <span>Tạm tính:</span>
                  <span>{totals.subtotal.toLocaleString('vi-VN', {
                    style: 'currency',
                    currency: 'VND'
                  })}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Thuế (10%):</span>
                  <span>{totals.tax.toLocaleString('vi-VN', {
                    style: 'currency',
                    currency: 'VND'
                  })}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Phí ship:</span>
                  <span>{totals.shippingFee.toLocaleString('vi-VN', {
                    style: 'currency',
                    currency: 'VND'
                  })}</span>
                </div>
                <hr />
                <div className="d-flex justify-content-between mb-3">
                  <strong>Tổng cộng:</strong>
                  <strong className="text-primary">
                    {totals.total.toLocaleString('vi-VN', {
                      style: 'currency',
                      currency: 'VND'
                    })}
                  </strong>
                </div>

                {!validation.isValid && (
                  <Alert variant="warning" className="mb-3">
                    <ul className="mb-0">
                      {validation.errors.map((error, index) => (
                        <li key={index}>{error}</li>
                      ))}
                    </ul>
                  </Alert>
                )}

                <div className="d-grid gap-2">
                  <Button
                    variant="primary"
                    size="lg"
                    as={Link}
                    to="/checkout"
                    disabled={!validation.isValid}
                  >
                    Tiến hành thanh toán
                  </Button>
                  <Button variant="outline-secondary" as={Link} to="/books">
                    <FaArrowLeft className="me-2" />
                    Tiếp tục mua sắm
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}
    </Container>
  );
};

export default CartPage;
