import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Navbar, Nav, Container, Badge, Dropdown, Form, InputGroup, Button } from 'react-bootstrap';
import { FaShoppingCart, FaUser, FaSearch, FaBars, FaBell } from 'react-icons/fa';

const Header = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [cartCount] = useState(3); // Mock cart count
  const [isAuthenticated] = useState(true); // Mock auth state
  const [user] = useState({ firstName: 'Nguy盻n Vﾄハ A' }); // Mock user

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/books?q=${encodeURIComponent(searchQuery.trim())}`);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <Navbar expand="lg" className="navbar-modern">
      <Container>
        <Navbar.Brand as={Link} to="/" className="navbar-brand-modern">
          <div className="brand-logo">
            <span className="logo-icon">沒</span>
            <span className="logo-text">BookStore</span>
          </div>
        </Navbar.Brand>

               {/* Search Bar - Right after logo */}
               <Form onSubmit={handleSearch} className="d-flex me-4">
          <InputGroup>
            <Form.Control
              type="search"
              placeholder="Tﾃｬm ki蘯ｿm sﾃ｡ch..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="form-control"
            />
            <Button variant="outline-light" type="submit">
              <FaSearch />
            </Button>
          </InputGroup>
        </Form>

        <Navbar.Toggle aria-controls="basic-navbar-nav">
          <FaBars />
        </Navbar.Toggle>

        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/" className="nav-link">
              Trang ch盻ｧ
            </Nav.Link>
            <Nav.Link as={Link} to="/books" className="nav-link">
              Sﾃ｡ch
            </Nav.Link>
            <Nav.Link as={Link} to="/categories" className="nav-link">
              Danh m盻･c
            </Nav.Link>
            <Nav.Link as={Link} to="/about" className="nav-link">
              Gi盻嬖 thi盻u
            </Nav.Link>
          </Nav>

                 <Nav>
                   <Nav.Link as={Link} to="/cart" className="position-relative">
                     <FaShoppingCart />
                     {cartCount > 0 && (
                       <Badge bg="danger" className="position-absolute top-0 end-0">
                         {cartCount}
                       </Badge>
                     )}
                   </Nav.Link>

            <Nav.Link className="position-relative">
              <FaBell />
              <Badge bg="warning" className="position-absolute top-0 end-0">
                2
              </Badge>
            </Nav.Link>

            {isAuthenticated ? (
              <Dropdown align="end">
                <Dropdown.Toggle as={Nav.Link} className="text-white">
                  <FaUser className="me-1" />
                  {user.firstName}
                </Dropdown.Toggle>
                       <Dropdown.Menu>
                         <Dropdown.Item as={Link} to="/profile">
                           Thﾃｴng tin cﾃ｡ nhﾃ｢n
                         </Dropdown.Item>
                         <Dropdown.Item as={Link} to="/orders">
                           ﾄ脆｡n hﾃng c盻ｧa tﾃｴi
                         </Dropdown.Item>
                         <Dropdown.Divider />
                         <Dropdown.Item onClick={handleLogout}>
                           ﾄ斉ハg xu蘯･t
                         </Dropdown.Item>
                       </Dropdown.Menu>
              </Dropdown>
            ) : (
              <>
                <Nav.Link as={Link} to="/login">ﾄ斉ハg nh蘯ｭp</Nav.Link>
                <Nav.Link as={Link} to="/register">ﾄ斉ハg kﾃｽ</Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
