import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button, Badge, Spinner, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { FaStar, FaShoppingCart, FaEye } from 'react-icons/fa';
import { useBookController, useCartController } from '../controllers';

const BookCard = ({ book }) => {
  const { addToCart } = useCartController();

  const handleAddToCart = async () => {
    await addToCart(book, 1);
  };

  return (
    <Card className="h-100 book-card shadow-sm">
      <div className="position-relative">
        <Card.Img
          variant="top"
          src={book.image || '/images/no-image.jpg'}
          alt={book.title}
          style={{ height: '250px', objectFit: 'cover' }}
        />
        {!book.isInStock() && (
          <Badge bg="danger" className="position-absolute top-0 end-0 m-2">
            Hết hàng
          </Badge>
        )}
      </div>
      <Card.Body className="d-flex flex-column">
        <Card.Title className="h6 mb-2" style={{ fontSize: '0.9rem' }}>
          {book.title}
        </Card.Title>
        <Card.Text className="text-muted small mb-2">
          Tác giả: {book.author}
        </Card.Text>
        <div className="d-flex align-items-center mb-2">
          <div className="d-flex align-items-center me-2">
            <FaStar className="text-warning me-1" />
            <span className="small">{book.getAverageRating()}</span>
          </div>
          <Badge bg="secondary" className="small">
            {book.category}
          </Badge>
        </div>
        <div className="mt-auto">
          <div className="h5 text-primary mb-3">
            {book.getFormattedPrice()}
          </div>
          <div className="d-flex gap-2">
            <Button
              variant="outline-primary"
              size="sm"
              as={Link}
              to={`/books/${book.id}`}
              className="flex-grow-1"
            >
              <FaEye className="me-1" />
              Xem
            </Button>
            <Button
              variant="primary"
              size="sm"
              onClick={handleAddToCart}
              disabled={!book.isInStock()}
              className="flex-grow-1"
            >
              <FaShoppingCart className="me-1" />
              Mua
            </Button>
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

const HomePage = () => {
  const { books, loading, error, fetchBooks, fetchBestSellingBooks, fetchNewestBooks } = useBookController();
  const [bestSellingBooks, setBestSellingBooks] = useState([]);
  const [newestBooks, setNewestBooks] = useState([]);

  useEffect(() => {
    const loadData = async () => {
      await fetchBooks({ limit: 8 });
      const bestSelling = await fetchBestSellingBooks(4);
      const newest = await fetchNewestBooks(4);
      setBestSellingBooks(bestSelling);
      setNewestBooks(newest);
    };
    loadData();
  }, [fetchBooks, fetchBestSellingBooks, fetchNewestBooks]);

  if (loading) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </Spinner>
        </div>
      </Container>
    );
  }

  return (
    <div>
      {/* Hero Section */}
      <section className="hero-section text-white py-5">
        <Container>
          <Row className="align-items-center">
            <Col lg={6}>
              <h1 className="display-4 fw-bold mb-4">
                Khám phá thế giới sách
              </h1>
              <p className="lead mb-4">
                Tìm kiếm và mua sắm hàng nghìn cuốn sách với chất lượng tốt nhất 
                và giá cả hợp lý tại BookStore.
              </p>
              <Button variant="light" size="lg" as={Link} to="/books" className="btn-modern">
                Khám phá ngay
              </Button>
            </Col>
            <Col lg={6} className="text-center">
              <div className="hero-icon">📚</div>
            </Col>
          </Row>
        </Container>
      </section>

      {/* Best Selling Books */}
      <section className="py-5">
        <Container>
          <Row className="mb-4">
            <Col>
              <h2 className="h3 fw-bold">Sách bán chạy</h2>
              <p className="text-muted">Những cuốn sách được yêu thích nhất</p>
            </Col>
          </Row>
          <Row>
            {bestSellingBooks.map(book => (
              <Col lg={3} md={6} className="mb-4" key={book.id}>
                <BookCard book={book} />
              </Col>
            ))}
          </Row>
        </Container>
      </section>

      {/* Newest Books */}
      <section className="py-5 bg-light">
        <Container>
          <Row className="mb-4">
            <Col>
              <h2 className="h3 fw-bold">Sách mới nhất</h2>
              <p className="text-muted">Những cuốn sách vừa được phát hành</p>
            </Col>
          </Row>
          <Row>
            {newestBooks.map(book => (
              <Col lg={3} md={6} className="mb-4" key={book.id}>
                <BookCard book={book} />
              </Col>
            ))}
          </Row>
        </Container>
      </section>

      {/* All Books */}
      <section className="py-5">
        <Container>
          <Row className="mb-4">
            <Col>
              <h2 className="h3 fw-bold">Tất cả sách</h2>
              <p className="text-muted">Khám phá bộ sưu tập sách đa dạng của chúng tôi</p>
            </Col>
          </Row>
          {error && (
            <Alert variant="danger" className="mb-4">
              {error}
            </Alert>
          )}
          <Row>
            {books.map(book => (
              <Col lg={3} md={6} className="mb-4" key={book.id}>
                <BookCard book={book} />
              </Col>
            ))}
          </Row>
          <Row>
            <Col className="text-center">
              <Button variant="outline-primary" as={Link} to="/books">
                Xem tất cả sách
              </Button>
            </Col>
          </Row>
        </Container>
      </section>

      {/* Features */}
      <section className="py-5 bg-primary text-white">
        <Container>
          <Row>
            <Col md={4} className="text-center mb-4">
              <div className="fs-1 mb-3">🚚</div>
              <h4>Giao hàng nhanh</h4>
              <p>Giao hàng trong 24h tại TP.HCM</p>
            </Col>
            <Col md={4} className="text-center mb-4">
              <div className="fs-1 mb-3">💎</div>
              <h4>Chất lượng tốt</h4>
              <p>Sách chính hãng, chất lượng cao</p>
            </Col>
            <Col md={4} className="text-center mb-4">
              <div className="fs-1 mb-3">🛡️</div>
              <h4>Bảo hành</h4>
              <p>Đổi trả miễn phí trong 7 ngày</p>
            </Col>
          </Row>
        </Container>
      </section>
    </div>
  );
};

export default HomePage;
