import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Spinner, Tab, Tabs } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import { FaEye, FaEyeSlash, FaGoogle, FaFacebook } from 'react-icons/fa';

const AuthPage = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('login');
  
  // Login form data
  const [loginData, setLoginData] = useState({
    email: '',
    password: ''
  });
  
  // Register form data
  const [registerData, setRegisterData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    agreeTerms: false
  });
  
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [rememberMe, setRememberMe] = useState(false);

  // Update URL when tab changes
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const tab = urlParams.get('tab');
    if (tab === 'register') {
      setActiveTab('register');
    } else {
      setActiveTab('login');
    }
  }, []);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    // Update URL
    const url = new URL(window.location);
    url.searchParams.set('tab', tab);
    window.history.pushState({}, '', url);
    setErrors({});
  };

  const handleLoginChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({
      ...prev,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleRegisterChange = (e) => {
    const { name, value, type, checked } = e.target;
    setRegisterData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateLoginForm = () => {
    const newErrors = {};

    if (!loginData.email) {
      newErrors.email = 'Email là bắt buộc';
    } else if (!/\S+@\S+\.\S+/.test(loginData.email)) {
      newErrors.email = 'Email không hợp lệ';
    }

    if (!loginData.password) {
      newErrors.password = 'Mật khẩu là bắt buộc';
    } else if (loginData.password.length < 6) {
      newErrors.password = 'Mật khẩu phải có ít nhất 6 ký tự';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateRegisterForm = () => {
    const newErrors = {};

    if (!registerData.fullName.trim()) {
      newErrors.fullName = 'Họ tên là bắt buộc';
    }

    if (!registerData.email) {
      newErrors.email = 'Email là bắt buộc';
    } else if (!/\S+@\S+\.\S+/.test(registerData.email)) {
      newErrors.email = 'Email không hợp lệ';
    }

    if (!registerData.password) {
      newErrors.password = 'Mật khẩu là bắt buộc';
    } else if (registerData.password.length < 6) {
      newErrors.password = 'Mật khẩu phải có ít nhất 6 ký tự';
    }

    if (registerData.password !== registerData.confirmPassword) {
      newErrors.confirmPassword = 'Mật khẩu xác nhận không khớp';
    }

    if (!registerData.phone) {
      newErrors.phone = 'Số điện thoại là bắt buộc';
    } else if (!/^[0-9]{10,11}$/.test(registerData.phone.replace(/\s/g, ''))) {
      newErrors.phone = 'Số điện thoại không hợp lệ';
    }

    if (!registerData.agreeTerms) {
      newErrors.agreeTerms = 'Bạn phải đồng ý với điều khoản sử dụng';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateLoginForm()) {
      return;
    }

    setLoading(true);
    
    // Simulate API call
    setTimeout(() => {
      // Mock successful login
      localStorage.setItem('user', JSON.stringify({
        id: 1,
        fullName: 'Nguyễn Văn A',
        firstName: 'Nguyễn Văn A',
        email: loginData.email,
        phone: '0123456789',
        address: '123 Đường ABC',
        city: 'TP. Hồ Chí Minh',
        zipCode: '700000',
        avatar: 'https://via.placeholder.com/100x100/6366f1/ffffff?text=User',
        joinDate: '2024-01-01',
        totalOrders: 15,
        totalSpent: 7500000
      }));
      
      setLoading(false);
      navigate('/');
    }, 1000);
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateRegisterForm()) {
      return;
    }

    setLoading(true);
    
    // Simulate API call
    setTimeout(() => {
      // Mock successful registration
      localStorage.setItem('user', JSON.stringify({
        id: Date.now(),
        fullName: registerData.fullName,
        firstName: registerData.fullName.split(' ')[0],
        email: registerData.email,
        phone: registerData.phone,
        address: '',
        city: '',
        zipCode: '',
        avatar: 'https://via.placeholder.com/100x100/6366f1/ffffff?text=User',
        joinDate: new Date().toISOString().split('T')[0],
        totalOrders: 0,
        totalSpent: 0
      }));
      
      setLoading(false);
      alert('Đăng ký thành công! Chào mừng bạn đến với BookStore!');
      navigate('/');
    }, 1000);
  };

  const handleSocialAuth = (provider) => {
    alert(`Đăng nhập với ${provider} (Chức năng sẽ được tích hợp sau)`);
  };

  return (
    <Container className="py-5">
      <Row className="justify-content-center">
        <Col md={8} lg={6} xl={5}>
          <Card className="shadow-lg border-0">
            <Card.Header className="bg-white border-0 text-center py-4">
              <h2 className="fw-bold text-primary mb-0">BookStore</h2>
              <p className="text-muted mb-0">Chào mừng bạn đến với thư viện sách trực tuyến</p>
            </Card.Header>
            
            <Card.Body className="p-4">
              <Tabs
                activeKey={activeTab}
                onSelect={handleTabChange}
                className="mb-4"
                fill
              >
                {/* Login Tab */}
                <Tab eventKey="login" title="Đăng nhập">
                  <Form onSubmit={handleLoginSubmit}>
                    <Form.Group className="mb-3">
                      <Form.Label>Email</Form.Label>
                      <Form.Control
                        type="email"
                        name="email"
                        value={loginData.email}
                        onChange={handleLoginChange}
                        placeholder="Nhập email của bạn"
                        isInvalid={!!errors.email}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.email}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3">
                      <Form.Label>Mật khẩu</Form.Label>
                      <div className="position-relative">
                        <Form.Control
                          type={showPassword ? 'text' : 'password'}
                          name="password"
                          value={loginData.password}
                          onChange={handleLoginChange}
                          placeholder="Nhập mật khẩu"
                          isInvalid={!!errors.password}
                        />
                        <Button
                          variant="link"
                          className="position-absolute end-0 top-50 translate-middle-y pe-3"
                          onClick={() => setShowPassword(!showPassword)}
                          style={{ border: 'none', background: 'none' }}
                        >
                          {showPassword ? <FaEyeSlash /> : <FaEye />}
                        </Button>
                      </div>
                      <Form.Control.Feedback type="invalid">
                        {errors.password}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <Form.Check
                        type="checkbox"
                        label="Ghi nhớ đăng nhập"
                        checked={rememberMe}
                        onChange={(e) => setRememberMe(e.target.checked)}
                      />
                      <Link to="/forgot-password" className="text-decoration-none">
                        Quên mật khẩu?
                      </Link>
                    </div>

                    <Button
                      type="submit"
                      variant="primary"
                      size="lg"
                      className="w-100 mb-3"
                      disabled={loading}
                    >
                      {loading ? (
                        <>
                          <Spinner size="sm" className="me-2" />
                          Đang đăng nhập...
                        </>
                      ) : (
                        'Đăng nhập'
                      )}
                    </Button>

                    <div className="text-center mb-3">
                      <span className="text-muted">Hoặc đăng nhập với</span>
                    </div>

                    <div className="d-grid gap-2 mb-3">
                      <Button
                        variant="outline-danger"
                        onClick={() => handleSocialAuth('Google')}
                      >
                        <FaGoogle className="me-2" />
                        Đăng nhập với Google
                      </Button>
                      <Button
                        variant="outline-primary"
                        onClick={() => handleSocialAuth('Facebook')}
                      >
                        <FaFacebook className="me-2" />
                        Đăng nhập với Facebook
                      </Button>
                    </div>

                    <div className="text-center">
                      <span className="text-muted">Chưa có tài khoản? </span>
                      <Button
                        variant="link"
                        className="p-0 text-decoration-none"
                        onClick={() => handleTabChange('register')}
                      >
                        Đăng ký ngay
                      </Button>
                    </div>
                  </Form>
                </Tab>

                {/* Register Tab */}
                <Tab eventKey="register" title="Đăng ký">
                  <Form onSubmit={handleRegisterSubmit}>
                    <Form.Group className="mb-3">
                      <Form.Label>Họ và tên</Form.Label>
                      <Form.Control
                        type="text"
                        name="fullName"
                        value={registerData.fullName}
                        onChange={handleRegisterChange}
                        placeholder="Nhập họ và tên"
                        isInvalid={!!errors.fullName}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.fullName}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3">
                      <Form.Label>Email</Form.Label>
                      <Form.Control
                        type="email"
                        name="email"
                        value={registerData.email}
                        onChange={handleRegisterChange}
                        placeholder="Nhập email của bạn"
                        isInvalid={!!errors.email}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.email}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3">
                      <Form.Label>Số điện thoại</Form.Label>
                      <Form.Control
                        type="tel"
                        name="phone"
                        value={registerData.phone}
                        onChange={handleRegisterChange}
                        placeholder="Nhập số điện thoại"
                        isInvalid={!!errors.phone}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.phone}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3">
                      <Form.Label>Mật khẩu</Form.Label>
                      <Form.Control
                        type="password"
                        name="password"
                        value={registerData.password}
                        onChange={handleRegisterChange}
                        placeholder="Nhập mật khẩu"
                        isInvalid={!!errors.password}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.password}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3">
                      <Form.Label>Xác nhận mật khẩu</Form.Label>
                      <div className="position-relative">
                        <Form.Control
                          type={showConfirmPassword ? 'text' : 'password'}
                          name="confirmPassword"
                          value={registerData.confirmPassword}
                          onChange={handleRegisterChange}
                          placeholder="Nhập lại mật khẩu"
                          isInvalid={!!errors.confirmPassword}
                        />
                        <Button
                          variant="link"
                          className="position-absolute end-0 top-50 translate-middle-y pe-3"
                          onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                          style={{ border: 'none', background: 'none' }}
                        >
                          {showConfirmPassword ? <FaEyeSlash /> : <FaEye />}
                        </Button>
                      </div>
                      <Form.Control.Feedback type="invalid">
                        {errors.confirmPassword}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3">
                      <Form.Check
                        type="checkbox"
                        name="agreeTerms"
                        checked={registerData.agreeTerms}
                        onChange={handleRegisterChange}
                        label={
                          <span>
                            Tôi đồng ý với{' '}
                            <Link to="/terms" className="text-decoration-none">
                              Điều khoản sử dụng
                            </Link>{' '}
                            và{' '}
                            <Link to="/privacy" className="text-decoration-none">
                              Chính sách bảo mật
                            </Link>
                          </span>
                        }
                        isInvalid={!!errors.agreeTerms}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.agreeTerms}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Button
                      type="submit"
                      variant="success"
                      size="lg"
                      className="w-100 mb-3"
                      disabled={loading}
                    >
                      {loading ? (
                        <>
                          <Spinner size="sm" className="me-2" />
                          Đang đăng ký...
                        </>
                      ) : (
                        'Đăng ký'
                      )}
                    </Button>

                    <div className="text-center mb-3">
                      <span className="text-muted">Hoặc đăng ký với</span>
                    </div>

                    <div className="d-grid gap-2 mb-3">
                      <Button
                        variant="outline-danger"
                        onClick={() => handleSocialAuth('Google')}
                      >
                        <FaGoogle className="me-2" />
                        Đăng ký với Google
                      </Button>
                      <Button
                        variant="outline-primary"
                        onClick={() => handleSocialAuth('Facebook')}
                      >
                        <FaFacebook className="me-2" />
                        Đăng ký với Facebook
                      </Button>
                    </div>

                    <div className="text-center">
                      <span className="text-muted">Đã có tài khoản? </span>
                      <Button
                        variant="link"
                        className="p-0 text-decoration-none"
                        onClick={() => handleTabChange('login')}
                      >
                        Đăng nhập ngay
                      </Button>
                    </div>
                  </Form>
                </Tab>
              </Tabs>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default AuthPage;
